import React, {useEffect, useState, useRef, useCallback} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from 'actions/productActions';
import Grid from '@mui/material/Grid'
import Button from '@mui/material/Button'
import useStyles from './styles'
import image from '../../assets/detailBackground.jpg'
import Fb from 'components/Fb'
import { Typography } from '@mui/material';
const Slider = () => {
  const dispatch = useDispatch();

  const productList = useSelector(state => state.productList);
  const { loading, error, products } = productList;

  useEffect(() => {
    dispatch(listProducts())
  }, [dispatch])
  const blackBurn = products && products.filter((item) => item.brand === 'Black Burn');
  const classes = useStyles()





 const containerRef = useRef()
 const imageHolderRef = useRef()
 const detailsRef = useRef()

  let slidesCount = blackBurn?.length

  let activeSlideIndex = 0;

  const changeSlide = useCallback((direction) => {
    if (direction === 'up') {
      activeSlideIndex++
      if (activeSlideIndex === slidesCount) {
        activeSlideIndex = 0;
      }
    }else if (direction === 'down') {
      activeSlideIndex--
      if (activeSlideIndex < 0) {
        activeSlideIndex = slidesCount - 1;
      }
    }
    const height = containerRef.current.clientHeight

    detailsRef.current.style.transform = `translateY(-${activeSlideIndex * height}px)`
    imageHolderRef.current.style.transform = `translateY(${activeSlideIndex * height}px)`

}, [detailsRef, imageHolderRef])

  return (
    <Grid 
      container
      spacing={2}
      mt={12}
      ref={containerRef}
      className={classes.container}
    >
      <Grid item md={5} className={classes.sidebar} style={{top: '0'}} ref={imageHolderRef} >
        {blackBurn?.map((product) => (
          <div 
            className={classes.sideBarDiv} 
            style={{background: `url(${product.image}) no-repeat center/cover`}} 
            key={product.id}
          >
            </div>
          ))}
      </Grid>
      <Grid item md={7} className={classes.mainSlide} ref={detailsRef} style={{background: `url(${image}) no-repeat center/cover`}} >
      {blackBurn?.map((product) => (
          <Fb 
            className={classes.mainSlideDiv} 
            key={product.id}
            column
          >

            <Typography variant='h3'>
              {product.name}
            </Typography>
            <Typography variant='p'>
              {product.description}
            </Typography>
              <Button>Add to Cart</Button>            
          </Fb>
          ))}
      </Grid>
    </Grid>
  )
};

export default Slider;
