import React, { useEffect } from 'react';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Message from 'components/Message';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart, removeFromCart } from 'actions/cartActions';
import Box from '@mui/material/Box';
import RestoreFromTrashIcon from '@mui/icons-material/RestoreFromTrash';
import MenuItem from '@mui/material/MenuItem';

import TableContainer from '@mui/material/TableContainer'
import Table from '@mui/material/Table'
import Select from '@mui/material/Select'
import TableHead from '@mui/material/TableHead'

import TableRow from '@mui/material/TableRow'
import TableCell from '@mui/material/TableCell'
import TableBody from '@mui/material/TableBody'
import Paper from '@mui/material/Paper'




import Fb from 'components/Fb';

import image from 'assets/detailBackground.jpg';

import useStyles from './styles';
import { IconButton, Typography } from '@mui/material';

const CartScreen = ({ match, location, history }) => {
  const classes = useStyles();
  const productId = match.params.id;

  const qty = location.search ? Number(location.search.split('=')[1]) : 1;

  const dispatch = useDispatch();

  const cart = useSelector(state => state.cart);
  const { cartItems } = cart;

  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  const removeFromCartHandler = id => {
    dispatch(removeFromCart(id));
  };

  const checkoutHandler = () => {
    history.push('/login?redirect=shipping');
  };
  return (
    <Grid
      container
      md={12}
      justifyContent='space-around'
      className={classes.container}
      style={{ background: `url(${image}) no-repeat center/cover` }}
    >
      <Grid item md={7}  >
        {/* <h1 style={{ color: 'white' }}>Корзина</h1> */}
        {cartItems.length === 0 ? (
          <Typography style={{ background: 'rgb(145 145 145 / 21%)', width: '400px' }}>
            Ваша корзина пуста 
            <Link style={{color: 'purple', marginLeft:'40px'}} to='/'>Перейти к покупкам</Link>
          </Typography>
        ) : (
          <TableContainer component={Paper} style={{maxHeight: '70vh', background: 'rgb(145 145 145 / 21%)'}}>
            <Table size='small' sx={{ minWidth: 650, }} aria-label='simple table'>
              <TableHead>
                <TableRow>
                  <TableCell></TableCell>
                  <TableCell align='left'>Наименование</TableCell>
                  <TableCell align='left'>Цена</TableCell>
                  <TableCell align='left'>Кол-во</TableCell>
                  <TableCell align='left'></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {cartItems.map(item => (
                  <TableRow
                    hover
                    key={item.product}
                    sx={{ '&:last-child td, &:last-child th': { border: 0 }, }}
                  >
                    <TableCell component='th' scope='row'>
                      <img src={item.image} alt="product_image" style={{width: '100px', height: '100px'}}/>
                    </TableCell>
                    <TableCell component={Link} to={`/product/${item.product}`} align='left'>{item.name}</TableCell>
                    <TableCell align='left'>{item.price}.000$</TableCell>
                    <TableCell align='left'>
                    <Select
                      variant='filled'
                      labelId='demo-simple-select-label'
                      style={{
                        color: 'white',
                        background: 'rgb(145 145 145 / 21%)',
                        height: '35px',
                        width: '70px',
                        borderRadius: '0',
                      }}
                      id='demo-simple-select'
                      value={item.qty}
                      onChange={(e) =>dispatch(addToCart(item.product, Number(e.target.value)))}
                    >
                      {[...Array(item.countInStock).keys()].map((x) => (
                        <MenuItem
                          value={x + 1}
                          key={x + 1}
                          style={{ color: 'black', borderRadius: '0' }}
                      >
                        <p>{x + 1}</p>
                      </MenuItem>
                      ))}
                    </Select>
                      </TableCell>
                    <TableCell align='left'>
                        <IconButton onClick={() => removeFromCartHandler(item.product)}>
                          <RestoreFromTrashIcon color='primary'/>
                        </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </Grid>
      <Grid item md={4} style={{ background: 'blue', maxHeight: '70vh' }}>
            <h2>
                Subtotal ({cartItems.reduce((acc, item) => acc + item.qty, 0)})
                items
              </h2>
              $
              {cartItems
                .reduce((acc, item) => acc + item.qty * item.price, 0)
                .toFixed(3)}
      </Grid>
    </Grid>
  );
};

export default CartScreen;
