import { createTheme, responsiveFontSizes } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    text: {
      primary: 'rgba(245, 245, 245, 0.7)',
    },
    primary: {
      main: '#ffffff',
    },
    secondary: {
      main: '#e61cb3',
    },
    // background: {
    //   default: '#000000'
    // }
  },
  components: {
    MuiButton: {
      styleOverrides: {
        contained: {
          color: '#ffffff',
          width: '280px',
          height: '35px',
          background: '#e61cb3',
          borderRadius: '20px',
          fontFamily: 'Copperplate',
          fontWeight: 'bold',
          fontSize: '13px',
          lineHeight: '9px',
          letterSpacing: '2px',
          ':hover': {
            background: '#c55b03',
          },
        },
        outlined: {
          color: '#ffffff',
          width: '280px',
          height: '35px',
          background: '#e61cb3',
          fontFamily: 'Copperplate',
          fontWeight: 'bold',
          fontSize: '13px',
          lineHeight: '9px',
          letterSpacing: '2px',
        },
        text: {
          color: '#ffffff',
          width: '100%',
          height: '37px',
          borderRadius: '0px 0px 10px 10px',
          background: '#e61cb3',
          fontFamily: 'GilroyMedium',
          fontSize: '10px',
          ':hover': {
            background: '#c55b03',
          },
        }
      },
    },
    MuiRating: {
      styleOverrides: {
        icon: {
          color: '#e61cb3',
        },
      },
    },
    MuiContainer: {
      styleOverrides: {
        maxWidthLg: {
          minWidth: '80%',
        },
        maxWidthMd: {
          minWidth: '80%',
        },
      },
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          background: 'linear-gradient(90deg, rgba(0,0,0,1) 32%, rgba(75,37,75,1) 72%, rgba(36,69,101,1) 100%);',
          backgroundColor: '#1a1a1a',
        }
      },
    }
  },
});

export default responsiveFontSizes(theme);
