import { createTheme, responsiveFontSizes } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    text: {
      primary: 'rgba(245, 245, 245, 0.7)',
    },
    primary: {
      main: '#ffffff',
    },
    secondary: {
      main: '#e61cb34d',
    },
    // background: {
    //   default: '#000000'
    // }
  },
  components: {
    MuiButton: {
      styleOverrides: {
        outlined: {
          width: '185px',
          height: '35px',
          border: 'none',
          borderRadius: '0',
          background: 'rgb(145 145 145 / 21%)',
          '&a': {
            fontFamily: 'Copperplate',
            fontWeight: 'normal',
            fontSize: '16px',
            lineHeight: '16px',
            color: 'white',
            textDecoration: 'none',
          },
          ':hover' : {
            background: 'rgb(145 145 145 / 15%)',
            border: 'none',
            transition: '0.3s',
            borderRadius: '0',
          }
        },
        text: {
          color: '#ffffff',
          width: '100%',
          height: '37px',
          borderRadius: '0px 0px 10px 10px',
          background: '#e61cb3',
          fontFamily: 'GilroyMedium',
          fontSize: '10px',
          ':hover': {
            background: '#c55b03',
          },
        },
      },
    },
    MuiContainer: {
      styleOverrides: {
        maxWidthLg: {
          minWidth: '100%',
        },
        maxWidthMd: {
          minWidth: '90%',
        },
      },
    },
    MuiTab: {
      styleOverrides: {
        textColorInherit: 'rgba(255, 255, 255, 0.8)',
        root: {
          background: 'rgb(145 145 145 / 21%)',
          fontFamily:'Copperplate',
          fontWeight: 'normal',
          fontSize: '20px',
          lineHeight: '21px',
          marginRight: '10px',
          marginBottom: '10px',
        }
      }
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          backgroundColor: 'rgb(0, 0, 1)',
        }
      },
    }
  },
});

export default responsiveFontSizes(theme);
