import { createTheme, responsiveFontSizes } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    text: {
      primary: 'rgba(245, 245, 245, 0.7)',
    },
    primary: {
      main: '#ffffff',
    },
    secondary: {
      main: '#E9822C',
    },
    background: {
      default: '#000000'
    }

  },
  components: {
    MuiButton: {
      styleOverrides: {
        contained: {
          color: '#ffffff',
          width: '280px',
          height: '35px',
          background: '#E9822C',
          borderRadius: '20px',
          fontFamily: 'Copperplate',
          fontWeight: 'bold',
          fontSize: '13px',
          lineHeight: '9px',
          letterSpacing: '2px',
          ':hover': {
            background: '#c55b03',
          },
        },
        text: {
          color: '#ffffff',
          width: '100%',
          height: '37px',
          borderRadius: '0px 0px 10px 10px',
          background: '#E9822C',
          fontFamily: 'GilroyMedium',
          fontSize: '10px',
          ':hover': {
            background: '#c55b03',
          },
        }
      },
    },
    MuiRating: {
      styleOverrides: {
        icon: {
          color: '#E9822C',
        },
      },
    },
    MuiContainer: {
      styleOverrides: {
        maxWidthLg: {
          minWidth: '80%',
        },
        maxWidthMd: {
          minWidth: '80%',
        },
      },
    },
    // MuiCssBaseline: {
    //   styleOverrides: {
    //     body: {
    //       background: ' linear-gradient(154deg, rgb(50 50 49 / 96%) 0%, #1a1a1a 36%);'
    //     }
    //   },
    // }
  },
});

export default responsiveFontSizes(theme);
