import React, { useState, useEffect, useMemo } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from 'store/actions/productActions';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Fb from 'components/Fb';
import Loader from 'components/Loader';
import Message from 'components/Message';

import Chip from '@mui/material/Chip';
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';

import useStyles from './styles';

import topArrow from '../../assets/topArrow.png';
import image from '../../assets/detailBackground.jpg';
import bottomArrow from '../../assets/bottomArrow.png';

const Slider = () => {
  const classes = useStyles();

  const [activeSlide, setActiveSlide] = useState(0);
  const dispatch = useDispatch();
  const { loading, error, products } = useSelector(state => state.productList);
  const slidesCount = useMemo(() => products?.length, [products]);

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

  useEffect(() => {
    const refreshIntervalId = setInterval(() => changeSlide('up'), 4000);
    return () => {
      clearInterval(refreshIntervalId);
    };
  });

  const changeSlide = direction => {
    setActiveSlide(prev => {
      if (direction === 'up') {
        return prev >= slidesCount - 1 ? 0 : prev + 1;
      } else if (direction === 'down') {
        return prev === 0 ? slidesCount - 1 : prev - 1;
      }
    });
  };

  const scrollDown = () => {
    window.scrollTo({
      top: 720,
      behavior: 'smooth',
    });
  };

  useEffect(() => toast(`${error}`), [error]);

  return (
    <>
        <Container
          className={classes.container}
          style={{ background: `url(${image}) no-repeat center/cover fixed` }}
        >
          <Grid container className={classes.mainGrid}>
            <img
              src={topArrow}
              alt='SlideTop'
              className={classes.topArrow}
              onClick={() => changeSlide('down')}
            />
            <img
              src={bottomArrow}
              alt='SlideTop'
              className={classes.bottomArrow}
              onClick={() => changeSlide('up')}
            />
            <Grid
              item
              md={4}
              ml={2}
              className={classes.sidebar}
              style={{ transform: `translateY(-${activeSlide * 100}%)` }}
            >
              {loading ? (
                <Loader />
              ) : (
                products.map(product => (
                  <div
                    className={classes.sideBarDiv}
                    key={product._id}
                    style={{
                      background: `url(${product.image}) no-repeat center/cover`,
                    }}
                  />
                ))
              )}
            </Grid>
            <Grid
              item
              md={8}
              className={classes.mainSlide}
              style={{
                top: `-${(slidesCount - 1) * 100}%`,
                transform: `translateY(${activeSlide * 100}%)`,
              }}
            >
              {loading ? (
                <Loader />
              ) : (
                [...products].reverse().map(product => (
                  <Fb
                    style={{ background: 'rgba(39,41,50, 0.30)' }}
                    className={classes.mainSlideDiv}
                    key={product._id}
                    column
                    pt={4}
                    pr={16}
                    pl={7}
                  >
                    <Typography
                      color='primary'
                      style={{
                        fontSize: '18px',
                        fontFamily: 'Burn',
                      }}
                    >
                      {product.brand}
                    </Typography>
                    <Typography
                      color='primary'
                      style={{
                        fontSize: '60px',
                        textTransform: 'uppercase',
                        fontWeight: 'bold',
                        lineHeight: '90%',
                        width: '318px',
                      }}
                    >
                      {product.name}
                    </Typography>
                    <Typography variant='p' className={classes.description} color='primary'>
                      {product.description}
                    </Typography>
                    <Fb mb={5} mt={1}>
                      <Stack direction='row' spacing={1}>
                        <Chip
                          label={`${product.type}`}
                          variant='filled'
                          style={{
                            fontFamily: 'Copperplate',
                            borderRadius: '0',
                            fontWeight: 'normal',
                            fontSize: '16px',
                            color: 'rbg(247, 243, 237)'
                          }}
                        />
                        <Chip
                          label={`${product.weight}`}
                          variant='filled'
                          style={{
                            fontFamily: 'Copperplate',
                            borderRadius: '0',
                            fontWeight: 'normal',
                            fontSize: '16px',
                          }}
                        />
                      </Stack>
                    </Fb>
                    <Button
                      component={Link}
                      to={`/product/${product._id}`}
                      variant='link'
                    >
                      Добавить в корзину
                    </Button>
                  </Fb>
                ))
              )}
            </Grid>
          </Grid>
          <KeyboardArrowDownIcon
            onClick={scrollDown}
            style={{
              fontSize: '60px',
              margin: '0 auto',
              display: 'block',
              cursor: 'pointer',
            }}
            color='primary'
          />
        </Container>
    </>
  );
};

export default Slider;
