import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import Fb from 'components/Fb';
import ProductDetails from './ProductDetails';
import ProductsImagePreview from './ProductsImagePreview';
import { listProducts } from '../../actions/productActions'
import LoadingButton from '@mui/lab/LoadingButton';
import {
  CarouselProvider,
  Slider,
  Slide,
  ButtonBack,
  ButtonNext,
  DotGroup,
} from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';

import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';


import useStyles from './styles';
import Loader from 'components/Loader';

const MainSlider = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(false);

  const dispatch = useDispatch();

  const productList = useSelector(state => state.productList);
  const { loading, error, products } = productList;

  useEffect(() => {
    dispatch(listProducts())
  }, [dispatch])
  const [activeProduct, setActiveProduct] = useState();

  const blackBurn = products && products.filter((item) => item.brand === 'Black Burn');

  useEffect(() => {
    setActiveProduct(products[0]);
  }, [products]);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  return (
    <Container style={{ height: '100vh', paddingTop: '120px' }}>
     {loading ? <Loader/> :  <Fb justifyBetween style={{ height: '100%' }}>
        <CarouselProvider
          isIntrinsicHeight
          className={classes.mainCarousel}
          totalSlides={blackBurn?.length}
        >
          <Grid container spacing={2}>
            <Grid item md={5}>
              <Fb justifyBetween column style={{ height: '480px' }}>
                    <ProductsImagePreview
                      products={blackBurn}
                      setActiveProduct={setActiveProduct}
                    />
              </Fb>
            </Grid>
           {loading 
           ? <LoadingButton loading variant="outlined">Submit</LoadingButton> 
           : error
           ? <h3>{error}</h3>
           : <Grid item md={7}>
              <Slider classNameAnimation={classes.animation} style={{background: 'red'}}>
                  {blackBurn?.map((product, index) => (
                    <Slide key={product._id} index={index}>
                      <ProductDetails product={product} />
                    </Slide>
                  ))}
                </Slider>
                    <Button variant="contained" onClick={handleOpen}>
                      Добавить в корзину
                    </Button>
            </Grid>
}
          </Grid>
        </CarouselProvider>

      </Fb>}
    </Container>
  );
};

export default MainSlider;
