import { makeStyles } from '@mui/styles';

export default makeStyles({
  form: {
    position: 'relative',
    transform: 'translate(-50%;-50%)',
    transition: 'all 1s',
    width:'50px',
    height:'50px',
    background: 'rgb(145 145 145 / 21%)',
    borderRadius: '25px',
    padding:'5px',
    '&:hover': {
      borderRadius: '2px',
      width:'200px',
      cursor: 'pointer',
      '&input': {
        display: 'block'
      },
      '&fa' : {
        backgroundColor: '#07051a',
        color:'#fff',
        right: '0',
      }
    }
},
input:{
    position: 'absolute',
    width:'100%',
    height:'42.5px',
    lineHeight: '30px',
    outline:'0',
    border:'0',
    display: 'none',
    fontSize: '1em',
    borderRadius: '20px',
    padding:'0px 20px',
},

fa: {
    top:'50%',
    right: '50%',
    transform:'translate(50%, -50%)',
    position: 'absolute',
    color:'white',
}
})
