import { makeStyles } from '@mui/styles';

export default makeStyles({
  cartSection: {
    borderRadius: '10px',
    border: '1px solid #524F4F',
    background: 'linear-gradient(135.48deg, #202529 47.89%, #0D0D0D 102.2%);'
  },
  imageHolder: {
    position: 'relative',
    borderRadius: '10px',
    width: '100%', 
    height: '318px',
    padding: '0 24px 4px 11px'
  },
  description: {
    display: '-webkit-box',
    position: 'absolute',
    bottom: '0',
    fontFamily: 'Copperplate',
    fontWeight: '300',
    color: 'white',
    fontSize: '15px',
    lineHeight: '17px',
    letterSpacing: '0.2px',
    textOverflow: 'ellipsis',
    overflow: 'hidden',
    whiteSpace: 'normal',
    WebkitLineClamp: '3',
    WebkitBoxOrient: 'vertical'
  },
  price: {
    fontFamily: 'Copperplate',
    color: 'white'
  },
  button: {
    fontWeight: 'bold!important',
    fontSize: '12px!important',
    lineHeight: '9px',
    letterSpacing: '2px'
  }
})
