import React from 'react';

import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';

import Rating from '@mui/material/Rating';
import Typography from '@mui/material/Typography';

import Fb from 'components/Fb';

import useStyles from './styles';

const ProductDetails = ({ product }) => {
  const classes = useStyles();
  return (
    <Fb px={1} mt={5} column style={{ color: 'white' }}>
      <Typography variant="h2" className={classes.headText}>
        {product.name}
      </Typography>
      <img
        style={{ width: '110px', height: '14px' }}
        src={product.logo}
        alt="BlackBurn"
      />
      <Fb mt={2}>
        <Rating value={product.rating} precision={0.5} readOnly />
      </Fb>
      <Fb my={2}>
        <Typography
          className={classes.description}
          align="justify"
          variant="p2"
        >
          {product.description}
        </Typography>
      </Fb>
      <Fb mb={10} style={{ marginBottom: '100px' }}>
        <Stack direction="row" spacing={1}>
          <Chip
            label={product.type}
            size="small"
            variant="outlined"
            style={{ fontFamily: 'Copperplate' }}
          />
          <Chip
            label={product.weight}
            size="small"
            variant="outlined"
            style={{ fontFamily: 'Copperplate' }}
          />
        </Stack>
      </Fb>
      <Fb>
      <Button variant="contained">Добавить в корзину</Button>
      </Fb>
    </Fb>
  );
};

export default ProductDetails;
