import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import Meta from '../components/Meta'
import { listProducts } from '../actions/productActions'
import ProductCart from 'components/ProductCard/ProductCard'
import sortProducts from '../utils/helpers'
import Container from '@mui/material/Container'
import Tabs from '@mui/material/Tabs'
import Tab from '@mui/material/Tab'
import Grid from '@mui/material/Grid'
import Fb from '../components/Fb'
import Slider from 'components/DoubleSlider/Slider'


const HomeScreen = ({ match }) => {
  const keyword = match.params.keyword

  const pageNumber = match.params.pageNumber || 1

  const dispatch = useDispatch()

  const productList = useSelector((state) => state.productList)
  const { loading, error, products, page, pages } = productList

  useEffect(() => {
    dispatch(listProducts(keyword, pageNumber))
  }, [dispatch, keyword, pageNumber])


  const [brand, setBrand] = useState(0);
  const sortedProducts = sortProducts(products)
  const productsEntries = Object.entries(sortedProducts);
  const handleChange = (_, index) => {
    setBrand(index);
  };

  return (
    <Container style={{height: '100vh'}}>
      <Meta />
      {!keyword ? (
        <Slider/>
      ) : (
        <Link to='/' className='btn btn-light'>
          Go Back
        </Link>
      )}
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <>
        <Container maxWidth='lg'>
        {productsEntries.length > 0 && (
        <>
        <Fb justifyAround alignCenter >
          <div style={{width: '360px', height: '1px', background: '#E9822C'}}/>
          <h1 style={{fontFamily: 'Copperplate'}}>Каталог</h1>
          <div style={{width: '360px', height: '1px', background: '#E9822C'}}/>
        </Fb>
      <Tabs
        value={brand}
        centered
        aria-label="basic tabs example"
        indicatorColor='secondary'
        onChange={handleChange}
      >
        {productsEntries?.map(([key]) => (
          <Tab key={key} id={key} icon={key} />
        ))}
      </Tabs>
      <Grid maxWidth='md' container spacing={3} style={{margin: '0 auto'}}>
        {productsEntries[brand][1].map((product) => (
          <Grid item xs={6} md={4} key={product._id}>
            <ProductCart product={product} />
          </Grid>
        ))}
      </Grid>
    </>
    )}
    </Container>
        </>
      )}
    </Container>
  )
}

export default HomeScreen
