import React, { useEffect, useState } from 'react';

import { PayPalButton } from 'react-paypal-button-v2';

import { useSelector, useDispatch } from 'react-redux';

import { getOrderDetails, payOrder, deliverOrder } from 'store/actions/orderActions';
import { ORDER_DELIVER_RESET, ORDER_PAY_RESET } from 'store/constants/orderConstants';

import axios from 'axios';

import Fb from 'components/Fb';
import Loader from 'components/Loader';
import Message from 'components/Message';

import List from '@mui/material/List';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button'
import Avatar from '@mui/material/Avatar';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';

import image from 'assets/detailBackground.jpg';

import useStyles from './styles';

const OrderScreen = ({ match, history }) => {
  const classes = useStyles();

  const orderId = match.params.id;
  const dispatch = useDispatch();

  const [sdkReady, setSdkReady] = useState(false);

  const orderDetails = useSelector(state => state.orderDetails);
  const { order, loading, error } = orderDetails;

  const orderPay = useSelector(state => state.orderPay);
  const { loading: loadingPay, success: successPay } = orderPay;

  const orderDeliver = useSelector(state => state.orderDeliver);
  const { loading: loadingDeliver, success: successDeliver } = orderDeliver;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  if (!loading) {
    order.itemsPrice = order.orderItems.reduce(
      (acc, item) => acc + item.price * item.qty,
      0
    );
  }

  useEffect(() => {
    if(!userInfo) {
      history.push('/login')
    }
  })

  useEffect(() => {
    const addPayPalScript = async () => {
      const { data: clientId } = await axios.get('/api/config/paypal');
      const script = document.createElement('script');
      script.type = 'text/javascript';
      script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}`;
      script.async = true;
      script.onload = () => {
        setSdkReady(true);
      };
      document.body.appendChild(script);
    };
    if (!order || successPay || successDeliver) {
      dispatch({ type: ORDER_PAY_RESET });
      dispatch({ type: ORDER_DELIVER_RESET });

      dispatch(getOrderDetails(orderId));
    } else if (!order.isPaid) {
      if (!window.paypal) {
        addPayPalScript();
      } else {
        setSdkReady(true);
      }
    }
  }, [dispatch, orderId, successPay, order, successDeliver]);

  const successPaymentHandler = paymentResult => {
    console.log(paymentResult);
    dispatch(payOrder(orderId, paymentResult));
  };

  const deliverHandler = () => {
    dispatch(deliverOrder(order));
  }


  return (
    <Container
      md={12}
      className={classes.container}
      style={{
        background: `url(${image}) no-repeat center/cover`,
      }}
    >
      {loading ? (
        <Loader />
      ) : (
        <>
          <h1>Order: {order.id}</h1>
          <Grid item md={12}>
            <Typography style={{ textAlign: 'center', fontSize: '30px' }}>
              Разместить заказ
            </Typography>
          </Grid>
          <Grid container justifyContent='space-around' pt={5}>
            <Grid item md={7} p={2} className={classes.formController}>
              <List sx={{ width: '100%', bgcolor: 'background.primary' }}>
                <Typography color='primary'>User: {order.user.name}</Typography>
                <Typography color='primary'>
                  Email: {order.user.email}
                </Typography>
                <Typography color='primary'>
                  Shipping: {order.shippingAddress.address},{' '}
                  {order.shippingAddress.city}, {order.shippingAddress.phone}
                </Typography>
                {order.isDelivered ? (
                  <Message variant='success'>
                    Delivered on {order.deliveredAt}
                  </Message>
                ) : (
                  <Message variant='danger'>Not Delivered</Message>
                )}
                <Divider color='primary' component='li' />
                <Typography color='primary'>
                  Payment Method: {order.paymentMethod}
                </Typography>
                {order.isPaid ? (
                  <Message variant='success'>Paid on {order.paidAt}</Message>
                ) : (
                  <Message variant='danger'>Not Paid</Message>
                )}

                <Divider color='primary' component='li' />
                <Typography color='primary'>Ordered Items:</Typography>
                {order.orderItems.length === 0 ? (
                  <Message>Order is Empty</Message>
                ) : (
                  <>
                    {order.orderItems.map((item, index) => (
                      <div key={index}>
                        <ListItem alignItems='center'>
                          <ListItemAvatar>
                            <Avatar
                              style={{ width: '50px', height: '50px' }}
                              alt='productImage'
                              src={`${item.image}`}
                            />
                          </ListItemAvatar>
                          <ListItemText>{item.name}</ListItemText>
                          <ListItemText
                            style={{ right: '0', position: 'absolute' }}
                          >
                            {item.qty}x${item.price} = ${item.qty * item.price}
                          </ListItemText>
                        </ListItem>
                        <Divider color='primary' component='li' />
                      </div>
                    ))}
                  </>
                )}
              </List>
            </Grid>
            <Grid className={classes.formController} item md={4} p={3}>
              <Fb column justifyAround style={{ height: '100%' }}>
                <h1
                  style={{
                    color: 'white',
                    textAlign: 'center',

                    fontSize: '20px',
                  }}
                >
                  Order Summary
                </h1>
                <Divider style={{ background: '#e61cb34d' }} />
                <Typography
                  style={{
                    fontSize: '20px',
                    textAlign: 'left',
                  }}
                >
                  Количество: {order.orderItems.length}
                </Typography>
                <Divider style={{ background: '#e61cb34d' }} />
                <Typography
                  style={{ fontFamily: 'Copperplate', fontSize: '30px' }}
                >
                  Цена: $ {order.itemsPrice}
                </Typography>
                <Divider style={{ background: '#e61cb34d' }} />
                <Typography
                  style={{ fontFamily: 'Copperplate', fontSize: '30px' }}
                >
                  Доставка: $ 1000
                </Typography>
                <Divider style={{ background: '#e61cb34d' }} />
                <Typography
                  style={{ fontFamily: 'Copperplate', fontSize: '30px' }}
                >
                  Общая сумма: {order.totalPrice}
                </Typography>
                <Divider style={{ background: '#e61cb34d' }} />
                {!order.isPaid && (
                  <div>
                    {loadingPay && <Loader />}
                    {!sdkReady ? (
                      <Loader />
                    ) : (
                      <PayPalButton
                        amount={order.totalPrice}
                        onSuccess={successPaymentHandler}
                      />
                    )}
                  </div>
                )}
                {loadingDeliver && <Loader/>}
              {userInfo && userInfo.isAdmin && order.isPaid && !order.isDelivered && (
                <Button
                  variant='link'
                  onClick={deliverHandler}
                >
                  Mark As Delivered
                </Button>
              )}

              </Fb>
            </Grid>
          </Grid>
        </>
      )}
    </Container>
  );
};

export default OrderScreen;
