import { createTheme, responsiveFontSizes } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    text: {
      primary: 'rgba(245, 245, 245, 0.7)',
    },
    primary: {
      main: '#ffffff',
    },
    secondary: {
      main: '#e61cb34d',
    },
    // background: {
    //   default: '#000000'
    // }
  },
  components: {
    MuiTable: {
      styleOverrides: {
        stickyHeader: {
          background: 'transparent',
        },
        root: {
          borderCollapse: 'unset',
          borderSpacing: '0 10px',
        }
      }
    },
    MuiTableRow: {
      styleOverrides: {
        root: {
          background: 'rgb(145 145 145 / 21%)',
        },
      },
    },
    MuiTableCell: {
      styleOverrides: {
        root: {
          border: '0',
          background: 'transparent',
        },
      },
    },
    MuiTableHead: {
      styleOverrides: {
        root: {
          background: 'red'
        },
      },
    },

    MuiButton: {
      styleOverrides: {
        link: {
          width: 'fit-content',
          fontFamily: 'Copperplate',
          fontWeight: 'normal',
          fontSize: '16px',
          lineHeight: '16px',
          color: 'rgba(255, 255, 255, 0.9)',
          height: '35px',
          borderRadius: '0',
          background: 'rgb(145 145 145 / 21%)',
          ':hover' : {
            background: 'rgb(145 145 145 / 15%)',
            borderRadius: '0',
            color: 'white',
          }
        },
        text: {
          color: '#ffffff',
          width: '100%',
          height: '37px',
          borderRadius: '0px 0px 10px 10px',
          background: '#e61cb3',
          fontFamily: 'GilroyMedium',
          fontSize: '10px',
          ':hover': {
            background: '#c55b03',
          },
        },
      },
    },
    MuiContainer: {
      styleOverrides: {
        maxWidthLg: {
          minWidth: '100%',
        },
        maxWidthMd: {
          minWidth: '90%',
        },
      },
    },
    MuiTab: {
      styleOverrides: {
        textColorInherit: 'rgba(255, 255, 255, 0.8)',
        root: {
          background: 'rgb(145 145 145 / 21%)',
          fontFamily:'Copperplate',
          fontWeight: 'normal',
          fontSize: '20px',
          lineHeight: '21px',
          marginRight: '10px',
          marginBottom: '10px',
        }
      }
    },
    MuiChip: {
      styleOverrides: {
        filled: {
          fontFamily: 'Copperplate',
          background: 'rgb(145 145 145 / 21%)',
          borderRadius: '0',
          fontWeight: 'normal',
          fontSize: '16px',
        }
      }
    },
    MuiInputLabel:{
      styleOverrides: {
        outlined: {
          color: 'rgba(255, 255, 255, 0.6)',
        }
      }
    },
    MuiOutlinedInput: {
      styleOverrides: {
        notchedOutline: {
          borderColor: 'rgba(255, 255, 255, 0.6)',
          borderRadius: '0'
        }
      }
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          backgroundColor: 'rgb(0, 0, 1)',
        }
      },
    }
  },
});

export default responsiveFontSizes(theme);
