      <div className={classes.searchBox} active={active}>
        <input className={classes.searchBoxInput} type="text" placeholder="Type to search.."/>
        <Fab onClick={() => setActive(true)} size='medium' color='secondary' className={classes.searchIcon}>
                <SearchIcon color='primary' />
              </Fab>
          <div className={classes.cancelIcon}>
            <i class="fas fa-times" onClick={(prev) => setActive(!prev)}/>
          </div>
        <div className={classes.searchData}></div>
      </div>
    </>
  )
}

export default SearchBox
