import React, {useEffect, useState, useRef} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from 'actions/productActions';
import Grid from '@mui/material/Grid'
import Button from '@mui/material/Button'
import useStyles from './styles'
const Slider = () => {
  const dispatch = useDispatch();

  const productList = useSelector(state => state.productList);
  const { loading, error, products } = productList;

  useEffect(() => {
    dispatch(listProducts())
  }, [dispatch])
  const blackBurn = products && products.filter((item) => item.brand === 'Black Burn');
  const classes = useStyles()

 const containerRef = useRef()
 const imageHolderRef = useRef()
 const detailsRef = useRef()

  let slidesCount = blackBurn.length

useEffect(() => {
  detailsRef.current.style.top = `-${(slidesCount - 1) * 100}vh`;

}, [detailsRef, slidesCount])
  let activeSlideIndex = 0;

  function changeSlide(direction) {
    if (direction === 'up') {
      activeSlideIndex++
      if (activeSlideIndex === slidesCount) {
        activeSlideIndex = 0;
      }
    }else if (direction === 'down') {
      activeSlideIndex--
      if (activeSlideIndex < 0) {
        activeSlideIndex = slidesCount - 1;
      }
    }

    const height = containerRef.current.clientHeight
    console.log(height)

    imageHolderRef.current.style.transform = `translateY(-${activeSlideIndex * height}px)`
    detailsRef.current.style.transform = `translateY(${activeSlideIndex * height}px)`
}

  return (
    <div ref={containerRef}style={{ height: '500px', width: '900px', position: 'relative', overflow: 'hidden', marginTop: '90px' }}>
      <div className={classes.sidebar} >
        {blackBurn.map((product) => (
          <div className={classes.imageHolder} ref={imageHolderRef} style={{background: `url(${product.image}) no-repeat center/cover`, width:'400px', height:'400px'}} key={product.id} ></div>
          ))}
      </div>
      <div className={classes.detailSlider}>
      {blackBurn.map((product) => (
          <div ref={detailsRef} style={{background: `url(${product.image}) no-repeat center/cover`, width:'400px', height:'400px'}} key={product.id}></div>
          ))}
      </div>
      <Button onClick={() => changeSlide('down')}>DOWN</Button>

      <Button onClick={() => changeSlide('down')}>UP</Button>

    </div>
  )
};

export default Slider;
