import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Container from '@mui/material/Container';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../../components/Message';
import Loader from '../../components/Loader';
import FormContainer from '../../components/FormContainer';
import { login } from '../../actions/userActions';
import image from 'assets/detailBackground.jpg';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Checkbox from '@mui/material/Checkbox';

import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import FormControlLabel from '@mui/material/FormControlLabel';

const LoginScreen = ({ location, history }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const dispatch = useDispatch();

  const userLogin = useSelector(state => state.userLogin);
  const { loading, error, userInfo } = userLogin;

  const redirect = location.search ? location.search.split('=')[1] : '/';

  useEffect(() => {
    if (userInfo) {
      history.push(redirect);
    }
  }, [history, userInfo, redirect]);

  const submitHandler = e => {
    e.preventDefault();
    dispatch(login(email, password));
  };

  return (
    <Grid
      container
      md={12}
      style={{
        background: `url(${image}) no-repeat center/cover`,
        height: '100vh',
        paddingTop: '120px',
      }}
    >
      <Container component='main' maxWidth='xs' style={{ background: 'rgba(99,99,99, 0.4)', height: '500px' }}>
        <Avatar sx={{ m: 1, bgcolor: 'secondary.main', margin: '0 auto', marginTop:'20px' }}>
          <LockOutlinedIcon />
        </Avatar>
        <h3 style={{fontFamily: 'Copperplate', fontSize: '30px', textAlign: 'center', color: 'white'}}>
          Sign in
        </h3>
        {error && <Message variant='danger'>{error}</Message>}
        {loading ? (
          <Loader />
        ) : (
          <Box
            component='form'
            onSubmit={submitHandler}
            noValidate
            color='primary'
            sx={{ mt: 1 }}
          >
            <TextField
              margin='normal'
              required
              color='primary'
              fullWidth
              id='email'
              label='Email Address'
              name='email'
              autoComplete='email'
              autoFocus
              type='email'
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
            <TextField
              margin='normal'
              required
              fullWidth
              name='password'
              label='Password'
              type='password'
              color='primary'
              id='password'
              value={password}
              onChange={e => setPassword(e.target.value)}
              autoComplete='current-password'
            />
            <FormControlLabel
              control={<Checkbox value='remember' color='primary' />}
              label='Remember me'
              color='primary'
            />
            <Button
              type='submit'
              fullWidth
              variant='link'
            >
              Sign In
            </Button>
            <Grid container>
              <Grid item>
                <Link
                  to={redirect ? `/register?redirect=${redirect}` : '/register'}
                  variant='body2'
                >
                  {"Don't have an account?  Sign Up"}
                </Link>
              </Grid>
            </Grid>
          </Box>
        )}
      </Container>
    </Grid>
  );
};

export default LoginScreen;
