import React, { useState } from 'react';
import useStyles from './styles';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import image from 'assets/detailBackground.jpg';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useSelector, useDispatch } from 'react-redux';
import { saveShippingAddress } from 'actions/cartActions';
import Typography from '@mui/material/Typography';
import CheckoutSteps from 'components/CheckoutSteps/CheckoutSteps';
import Message from 'components/Message';
import { Link } from 'react-router-dom';
import Paper from '@mui/material/Paper';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import List from '@mui/material/List';
import Fb from 'components/Fb'
const PlaceOrder = ({ history }) => {
  const classes = useStyles();

  const cart = useSelector(state => state.cart);
console.log(cart)
  // Calculate prices
  cart.itemsPrice = cart.cartItems.reduce((acc, item) => acc + item.price * item.qty, 0);
  cart.shippingPrice = 1000
  cart.totalPrice = (Number(cart.itemsPrice) + Number(cart.shippingPrice)).toFixed(2)

  const placeOrderHandler = () =>{ 
    console.log('hey')
  }

  return (
    <Container
      md={12}
      className={classes.container}
      style={{
        background: `url(${image}) no-repeat center/cover`,
      }}
    >
      <Grid item md={12}>
        <Typography style={{ textAlign: 'center', fontSize: '30px' }}>
          Разместить заказ
        </Typography>
        <CheckoutSteps step1 step2 step3 step4 />
      </Grid>
      <Grid container md={12} justifyContent='space-around' pt={5}>
        <Grid item md={7} p={2} className={classes.formController}>
          <List sx={{ width: '100%', bgcolor: 'background.primary' }}>
            <Typography color='primary'>Shipping: {cart.shippingAddress.address}, {cart.shippingAddress.city},{' '}
            {cart.shippingAddress.phone}</Typography>
            <Divider color='primary' component='li' />
            <Typography color='primary'>Payment Method: {cart.paymentMethod.paymentMethod}</Typography>

              <Divider color='primary' component='li' />
            <Typography color='primary'>Ordered Items:</Typography>
            {cart.cartItems.length === 0 ? (
              <Message>Your Card is Empty</Message>
            ) : (
              <>
                {cart.cartItems.map((item,index) => (
                  <>
                  <ListItem alignItems='center'>
                  <ListItemAvatar >
                    <Avatar
                    style={{width: '50px', height: '50px'}}
                      alt='productImage'
                      src={`${item.image}`}
                    />
                  </ListItemAvatar>
                  <ListItemText >{item.name}</ListItemText>
                  <ListItemText style={{right: '0', position: 'absolute'}}>{item.qty}x${item.price} = ${item.qty * item.price}</ListItemText>
                </ListItem>
                <Divider color='primary' component='li' />
                  </>
                ))}
              </>
            )}
          </List>

        </Grid>
        <Grid className={classes.formController} item md={3}>
        <Fb column justifyAround >
          <h1
            style={{
              color: 'white',
              textAlign: 'center',

              fontSize: '20px',
            }}
          >
            Order Summary
          </h1>
          <Divider style={{ background: '#e61cb34d' }} />
          <Typography
            style={{
              fontSize: '20px',
              textAlign: 'left',
            }}
          >
            Количество: {cart.cartItems.length}
          </Typography>
          <Divider style={{ background: '#e61cb34d' }} />
          <Typography style={{ fontFamily: 'Copperplate', fontSize: '30px' }}>
            Цена: $ {cart.itemsPrice}
          </Typography>
          <Divider style={{ background: '#e61cb34d' }} />
          <Typography style={{ fontFamily: 'Copperplate', fontSize: '30px' }}>
            Доставка: $ 1000 
          </Typography>
          <Divider style={{ background: '#e61cb34d' }} />
          <Typography style={{ fontFamily: 'Copperplate', fontSize: '30px' }}>
            Общая сумма: {cart.totalPrice}
          </Typography>
          <Divider style={{ background: '#e61cb34d' }} />

            <Button
              style={{
                width: '100%',
                background: '#e61cb34d',
                marginBottom: '10px',
              }}
              type='button'
              variant='link'
              className='btn-block'
              onClick={placeOrderHandler}
            >
              Place Order
            </Button>
        </Fb>
        </Grid>
      </Grid>
    </Container>
  );
};

export default PlaceOrder;
