import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import { listProducts, deleteProduct } from 'actions/productActions';

import Loader from 'components/Loader';
import Message from 'components/Message';

import Table from '@mui/material/Table';
import Button from '@mui/material/Button';
import TableRow from '@mui/material/TableRow';
import Container from '@mui/material/Container';
import TableHead from '@mui/material/TableHead';
import TableCell from '@mui/material/TableCell';
import TableBody from '@mui/material/TableBody';
import Typography from '@mui/material/Typography';

import image from 'assets/detailBackground.jpg';

import useStyles from './styles';

const ProductListScreen = ({ match, history }) => {
  const classes = useStyles();
  const dispatch = useDispatch();

  const productList = useSelector(state => state.productList);
  const { loading, error, products } = productList;

  const productDelete = useSelector(state => state.productDelete);
  const { loading: loadingDelete, error: errorDelete, success: successDelete } = productDelete;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    if(userInfo && userInfo.isAdmin) {
      dispatch(listProducts());
    } else {
      history.push('/login')
    }
  }, [dispatch, history, userInfo, successDelete]);

  const deleteHandler = (id) => {
    if(window.confirm('Are you sure')) {
      dispatch(deleteProduct(id));
    }
  }

  const createProductHandler = () => {

  }

  return (
    <Container
      md={12}
      className={classes.container}
      style={{ background: `url(${image}) no-repeat center/cover` }}
    >
      <Typography style={{ fontSize: '40px', textAlign: 'center' }}>
        Products
      </Typography>
      <Button variant='link' onClick={createProductHandler}>
          Create Product
      </Button>
      {loadingDelete && <Loader/>}
      {errorDelete && <Message>{errorDelete}</Message>}
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Table size='small' sx={{ minWidth: 650 }} aria-label='simple table'>
          <TableHead>
            <TableRow>
              <TableCell align='center'>ID</TableCell>
              <TableCell align='center'>Название</TableCell>
              <TableCell align='center'>Цена</TableCell>
              <TableCell align='center'>Категория</TableCell>
              <TableCell align='center'>Бредн</TableCell>
              <TableCell align='center'></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products.map(product => (
              <TableRow key={product._id}>
                <TableCell align='center'>{product._id}</TableCell>
                <TableCell align='center'>
                  {product.name}
                </TableCell>
                <TableCell align='center'>{product.price}</TableCell>
                <TableCell align='center'>{product.category}</TableCell>
                <TableCell align='center'>{product.brand}</TableCell>
                <TableCell align='center'>
                <Button
                    component={Link}
                    to={`/admin/product/${product._id}/edit`}
                    className='btn-sm'
                    variant='light'
                  >
                   Изменить
                  </Button>
                  <Button
                    className='btn-sm'
                    variant='light'
                    onClick={() => deleteHandler(product._id)}
                  >
                   Удалить
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      )}
    </Container>
  );
};

export default ProductListScreen;
