import React, { useEffect } from 'react'
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Message from 'components/Message'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { addToCart, removeFromCart } from 'actions/cartActions'
import Box from '@mui/material/Box'
import Fb from 'components/Fb'

import image from 'assets/detailBackground.jpg';

import useStyles from './styles';
import { Typography } from '@mui/material';

const CartScreen = ({ match, location, history }) => {
  const classes = useStyles();
  const productId = match.params.id

  const qty = location.search ? Number(location.search.split('=')[1]) : 1

  const dispatch = useDispatch()

  const cart = useSelector((state) => state.cart)
  const { cartItems } = cart

  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty))
    }
  }, [dispatch, productId, qty])

  const removeFromCartHandler = (id) => {
    dispatch(removeFromCart(id))
  }

  const checkoutHandler = () => {
    history.push('/login?redirect=shipping')
  }
  return (
    <Container
      container
      md={12}
      className={classes.container}
      style={{background: `url(${image}) no-repeat center/cover`,}}
    >
      <Grid item md={7} p={1} style={{background: 'red'}}>
      <h1 style={{color: 'white'}}>Shopping Cart</h1>
        {cartItems.length === 0 ? (
          <Typography style={{background: 'rgb(145 145 145 / 21%)'}}>
            Your cart is empty <Link to='/'>Go Back</Link>
          </Typography>
        ) : (
          <Fb column justifyBetween>
            {cartItems.map((item) => (
              <Fb key={item.product} justifyBetween alignCenter style={{background: 'black', marginTop: '20px'}}>
                  <div>
                    <img src={item.image} alt={item.name} style={{width: '100px', height: '100px'}} />
                  </div>
                 <div>
                 <Typography >
                    <Link style={{color: 'white'}} to={`/product/${item.product}`}>{item.name}</Link>
                  </Typography>
                 </div>
                  <Typography>${item.price}.000</Typography>
                  <div >
                    <div
                      as='select'
                      value={item.qty}
                      onChange={(e) =>
                        dispatch(
                          addToCart(item.product, Number(e.target.value))
                        )
                      }
                    >
                      {[...Array(item.countInStock).keys()].map((x) => (
                        <option key={x + 1} value={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </div>
                  </div>
                  <div >
                    <button
                      type='button'
                      variant='light'
                      onClick={() => removeFromCartHandler(item.product)}
                    >
                      <i className='fas fa-trash'></i>
                    </button>
                  </div>
              </Fb>
            ))}
          </Fb>
        )}
      </Grid>
      <Grid md={2} style={{background: 'blue'}}>

      </Grid>
    </Container>
  )
}

export default CartScreen
