import React from 'react';
import { Link } from 'react-router-dom'
import Fb from 'components/Fb';
import Box from '@mui/material/Box';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid'
import useStyles from './styles';

const ProductCart = ({ product }) => {
  const classes = useStyles();

  return (
    <Grid item className={classes.cartSection}>
      <div 
        className={classes.imageHolder}
        style={{
          width: '100%',
          height: '90%',
          background: `url(${product.image}) no-repeat center/cover`,
        }}
      />
      <Fb px={1.5} py={2} justifyBetween alignCenter column style={{margin: '0 auto'}}>
        <Typography >
          {product.name}
        </Typography>
        {product.countInStock > 0 ? (
          <Typography>
            {`${product.price}.000 AMD`}
          </Typography>
        ) : (
          'Out of Stock'
        )}
          <Button variant='outlined' className={classes.button}><Link to={`/product/${product._id}`}>Добавить в корзину</Link></Button>
      </Fb>
    </Grid>
  );
};

export default ProductCart;
