import React, { useState, useEffect, useMemo } from 'react';

import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from 'actions/productActions';

import Fb from 'components/Fb';
import Rating from 'components/Rating';

import Chip from '@mui/material/Chip';
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container'
import Typography from '@mui/material/Typography';

import useStyles from './styles';
import image from '../../assets/detailBackground.jpg';
import topArrow from '../../assets/topArrow.png';
import bottomArrow from '../../assets/bottomArrow.png';

const Slider = () => {
  const classes = useStyles();
  const [activeSlide, setActiveSlide] = useState(0);
  const dispatch = useDispatch();
  const { loading, error, products } = useSelector(state => state.productList);
  const slidesCount = useMemo(() => products?.length, [products]);

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

  const changeSlide = direction => {
    setActiveSlide(prev => {
      if (direction === 'up') {
        return prev >= slidesCount - 1 ? 0 : prev + 1;
      } else if (direction === 'down') {
        return prev === 0 ? slidesCount - 1 : prev-1;
      }
    })
  };

  return (
    <Container   
      container
      className={classes.container}
      style={{background: `url(${image}) no-repeat center/cover`}}
    >
      <Grid 
      container
      md={11}
      className={classes.mainGrid}
      >
      <img
        src={topArrow} 
        alt ='SlideTop' 
        className={classes.topArrow} 
        onClick={() => changeSlide('down')}
      />
       <img
          src={bottomArrow}
          alt ='SlideTop'
          className={classes.bottomArrow}
          onClick={() => changeSlide('up')}
        />
      <Grid
        item
        md={4}
        ml={2}
        className={classes.sidebar}
        style={{ transform: `translateY(-${activeSlide * 100}%)` }}
      >
        {products && products?.map(product => (
          <div
            className={classes.sideBarDiv}
            key={product.id}
            style={{
              background: `url(${product.image}) no-repeat center/cover`,
            }}
          />
        ))}
      </Grid>
      <Grid 
        item 
        md={8} 
        className={classes.mainSlide} 
        style={{
          top: `-${(slidesCount - 1) * 100}%`,
          transform: `translateY(${activeSlide * 100}%)`,
        }}
      >
        {[...products].reverse().map(product => (
          <Fb
            style={{ background: 'rgba(99,99,99, 0.15)' }}
            className={classes.mainSlideDiv}
            key={product.id}
            column
            pt={12}
            p={6}
          >
            <Typography
              style={{
                color: 'rgba(255,255,255, 0.8)',
                fontSize: '18px',
                fontFamily: 'Burn',
                marginBottom: '10px',
              }}
            >
              {product.brand}
            </Typography>
            <Typography
              style={{ color: 'white', fontSize: '20px', fontFamily: 'Burn' }}
            >
              {product.name}
            </Typography>
            <Typography
              variant='p'
              style={{
                fontFamily: 'Copperplate',
                fontSize: '17px',
                marginBottom: '10px',
              }}
            >
              {product.description}
            </Typography>
            <Fb mb={6}>
              <Stack direction='row' spacing={1}>
                <Chip
                  label='Сладкий'
                  size='small'
                  variant='outlined'
                  style={{ fontFamily: 'Copperplate' }}
                />
                <Chip
                  label='200g'
                  size='small'
                  variant='outlined'
                  style={{ fontFamily: 'Copperplate' }}
                />
              </Stack>
            </Fb>
            <Button
              variant='outlined'
            >
              Добавить в корзину
            </Button>
          </Fb>
        ))}
      </Grid>
    </Grid>
    </Container>
  );
};

export default Slider;
