import React, { useEffect } from 'react';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Message from 'components/Message';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart, removeFromCart } from 'actions/cartActions';
import Box from '@mui/material/Box';
import TableContainer from '@mui/material/TableContainer'
import Table from '@mui/material/Table'
import TableHead from '@mui/material/TableHead'

import TableRow from '@mui/material/TableRow'
import TableCell from '@mui/material/TableCell'
import TableBody from '@mui/material/TableBody'
import Paper from '@mui/material/Paper'




import Fb from 'components/Fb';

import image from 'assets/detailBackground.jpg';

import useStyles from './styles';
import { Typography } from '@mui/material';

const CartScreen = ({ match, location, history }) => {
  const classes = useStyles();
  const productId = match.params.id;

  const qty = location.search ? Number(location.search.split('=')[1]) : 1;

  const dispatch = useDispatch();

  const cart = useSelector(state => state.cart);
  const { cartItems } = cart;

  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  const removeFromCartHandler = id => {
    dispatch(removeFromCart(id));
  };

  const checkoutHandler = () => {
    history.push('/login?redirect=shipping');
  };
  return (
    <Container
      container
      md={12}
      className={classes.container}
      style={{ background: `url(${image}) no-repeat center/cover` }}
    >
      <Grid item md={7} p={1} >
        <h1 style={{ color: 'white' }}>Корзина</h1>
        {cartItems.length === 0 ? (
          <Typography style={{ background: 'rgb(145 145 145 / 21%)' }}>
            Your cart is empty <Link to='/'>Go Back</Link>
          </Typography>
        ) : (
          <TableContainer component={Paper} style={{maxHeight: 460, background: 'rgb(145 145 145 / 21%)'}}>
            <Table size='small' sx={{ minWidth: 650, }} aria-label='simple table'>
              <TableHead>
                <TableRow>
                  <TableCell></TableCell>
                  <TableCell align='left'>Наименование</TableCell>
                  <TableCell align='left'>Цена</TableCell>
                  <TableCell align='left'>Кол-во</TableCell>
                  <TableCell align='left'>Сумма</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {cartItems.map(item => (
                  <TableRow
                    hover
                    key={item.product}
                    sx={{ '&:last-child td, &:last-child th': { border: 0 }, }}
                  >
                    <TableCell component='th' scope='row'>
                      <img src={item.image} alt="product_image" style={{width: '100px', height: '100px'}}/>
                    </TableCell>
                    <TableCell component={Link} to={`/product/${item.product}`} align='left'>{item.name}</TableCell>
                    <TableCell align='left'>{item.price}.000$</TableCell>
                    <TableCell align='left'>{item.qty}</TableCell>
                    <TableCell align='left'>{item.countInStock}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </Grid>
      <Grid md={2} style={{ background: 'blue' }}></Grid>
    </Container>
  );
};

export default CartScreen;
