import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import Button from '@mui/material/Button';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import { Link } from 'react-router-dom'

import Message from 'components/Message';
import Loader from '../../components/Loader';
import Fb from 'components/Fb';
import {
  listProductDetails,
  createProductReview,
} from '../../actions/productActions';
import { PRODUCT_CREATE_REVIEW_RESET } from '../../constants/productConstants';

import useStyles from './styles';
import Grid from '@mui/material/Grid';
import image from 'assets/detailBackground.jpg';

const ProductDetailSection = ({ history, match }) => {
  const classes = useStyles();
  const [qty, setQty] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');

  const dispatch = useDispatch();

  const productDetails = useSelector(state => state.productDetails);
  const { loading, error, product } = productDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  const productReviewCreate = useSelector(state => state.productReviewCreate);
  const {
    success: successProductReview,
    loading: loadingProductReview,
    error: errorProductReview,
  } = productReviewCreate;

  useEffect(() => {
    if (successProductReview) {
      setRating(0);
      setComment('');
    }
    if (!product._id || product._id !== match.params.id) {
      dispatch(listProductDetails(match.params.id));
      dispatch({ type: PRODUCT_CREATE_REVIEW_RESET });
    }
  }, [dispatch, match, successProductReview]);

  const addToCartHandler = () => {
    history.push(`/cart/${match.params.id}?qty=${qty}`);
  };

  const submitHandler = e => {
    e.preventDefault();
    dispatch(
      createProductReview(match.params.id, {
        rating,
        comment,
      })
    );
  };

  return (
    <Grid
      container
      justifyContent='space-around'
      md={12}
      style={{
        background: `url(${image}) no-repeat center/cover`,
        paddingTop: '120px',
        paddingBottom: '120px',
      }}
    >

      {error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <>
        {/* <Button component={Link} variant='link' to='/'>
          Назад
        </Button> */}
          <Grid md={6} item className={classes.details}>
            <span className={classes.brand}>{product.brand}</span>
            <h2 className={classes.title}>{product.name}</h2>
            <p className={classes.description}>{product.description}</p>
            <Fb mb={5} mt={1}>
              <Stack direction='row' spacing={1}>
                <Chip
                  label={`${product.type}`}
                  variant='filled'
                />
                <Chip
                  label={`${product.weight}`}
                  variant='filled'
                />
              </Stack>
            </Fb>
            <Fb alignCenter>
              <Button
                variant='link'
                disabled={product?.countInStock < 1}
                onClick={addToCartHandler}
                style={{marginRight: '20px'}}
              >
                Добавить в корзину
              </Button>
              {product.countInStock > 0 ? (
                  <div>
                    <Select
                    variant='filled'
                    labelId='demo-simple-select-label'
                    style={{color: 'white', background: 'rgb(145 145 145 / 21%)', height: '35px', width: '70px'}}
                    id='demo-simple-select'
                    value={qty}
                    onChange={e => setQty(e.target.value)}
                  >
                    {[...Array(product.countInStock).keys()].map(x => (
                      <MenuItem value={x + 1} key={x + 1} style={{ color: 'black', borderRadius: '0'}}>
                        <p>{x + 1}</p>
                      </MenuItem>
                    ))}
                  </Select>
                  </div>
              ) : (
                'Out of Stock'
              )}
            </Fb>
          </Grid>
          <Grid item md={4}>
              <div
                style={{
                  background: `url(${product.image}) no-repeat center/cover`,
                  width: '100%',
                  height: '100%',
                }}
              ></div>
          </Grid>
        </>
      )}
    </Grid>
  );
};

export default ProductDetailSection;
