import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';

import { logout } from '../../actions/userActions';

import { NavDropdown } from 'react-bootstrap';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab'
import IconButton from '@mui/material/IconButton';
import LogoutIcon from '@mui/icons-material/Logout';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import Fb from 'components/Fb'
import logo from 'assets/logo.svg'
import Tooltip from '@mui/material/Tooltip'
import Avatar from '@mui/material/Avatar';
import MenuItem from '@mui/material/MenuItem';
import Menu from '@mui/material/Menu'

import useStyles from './styles';

import russia from 'assets/russia.png';

const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  const logoutHandler = () => {
    dispatch(logout());
  };
  const [isScrolled, setIsScrolled] = useState(false);
  const classes = useStyles({ isScrolled });

  const handleScrollChange = () => {
    const top = document.body.scrollTop || document.documentElement.scrollTop;
    setIsScrolled(top > 100);
  };

  const [anchorEl, setAnchorEl] = useState(null);

  const handleOpenAccountSettings = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleCloseAccountSettings = () => {
    setAnchorEl(null);
  };

  const haha = () => {
    return (
      <>
      <Link to='/profile'>
       <Fab
            color='primary'
            size='large'
            style={{
              background: 'rgb(145 145 145 / 18%)',
              marginRight: '10px',
            }}
          >
            <PersonOutlineIcon color='primary'/>
          </Fab>
        </Link>
        <Fab
          onClick={logoutHandler}
          color='primary'
          size='large'
          style={{
            background: 'rgb(145 145 145 / 18%)',
            marginRight: '10px',
          }}
        >
          <LogoutIcon color='primary'/>
        </Fab>
      </>
    )
  }

  useEffect(() => {
    window.addEventListener('scroll', handleScrollChange);
    return () => {
      window.removeEventListener('scroll', handleScrollChange);
    };
  }, []);

  return (
    <Grid
      container
      px={8}
      py={2}
      alignItems='center'
      justifyContent='space-between'
      className={classes.header}
    >
      <Grid item md={3}>
        <Link to='/'>
          <img src={logo} alt="logo" className={classes.logo}/>
        </Link>
      </Grid>
      {/* <Route render={({ history }) => <SearchBox history={history} />} /> */}
      <Grid item>
        <Fb>
        <Link to='/cart'>
          <Fab
            size='large'
            color='primary'
            style={{
              background: 'rgb(145 145 145 / 18%)',
              marginRight: '10px',
            }}
          >
            <ShoppingCartIcon color='primary'/>
          </Fab>
        </Link>
        {userInfo ? (
          <>
          <Tooltip title="Account settings">
          <IconButton onClick={() => alert('ehhe')} size="medium" style={{background: 'rgb(145 145 145 / 18%)'}} >
            <Avatar style={{background: 'transparent', fontSize: '25px'}}>{userInfo.name.substring(0,1)}</Avatar>
          </IconButton>
        </Tooltip>
        <Menu
        anchorEl={anchorEl}
        open={anchorEl}
        onClose={handleOpenAccountSettings}
        onClick={handleCloseAccountSettings}
        PaperProps={{
          elevation: 0,
          sx: {
            overflow: 'visible',
            filter: 'drop-shadow(0px 2px 8px rgba(0,0,0,0.32))',
            mt: 1.5,
            '& .MuiAvatar-root': {
              width: 32,
              height: 32,
              ml: -0.5,
              mr: 1,
            },
            '&:before': {
              content: '""',
              display: 'block',
              position: 'absolute',
              top: 0,
              right: 14,
              width: 10,
              height: 10,
              bgcolor: 'background.paper',
              transform: 'translateY(-50%) rotate(45deg)',
              zIndex: 0,
            },
          },
        }}
        transformOrigin={{ horizontal: 'right', vertical: 'top' }}
        anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
      >
        <MenuItem>
          <Avatar /> Profile
        </MenuItem>
        <MenuItem>
          <Avatar /> My account
        </MenuItem>
        </Menu>
          </>
        ) : (
          <Link to='/login'>
            <Fab
              onClick={logoutHandler}
              color='primary'
              size='large'
              style={{
                background: 'rgb(145 145 145 / 18%)',
                marginRight: '10px',
              }}
            >
              <PersonOutlineIcon color='primary'/>
            </Fab>
          </Link>
        )}
        {userInfo && userInfo.isAdmin && (
          <NavDropdown title='Admin' id='adminmenu'>
            <Link to='/admin/userlist'>
              <NavDropdown.Item>Users</NavDropdown.Item>
            </Link>
            <Link to='/admin/productlist'>
              <NavDropdown.Item>Products</NavDropdown.Item>
            </Link>
            <Link to='/admin/orderlist'>
              <NavDropdown.Item>Orders</NavDropdown.Item>
            </Link>
          </NavDropdown>
        )}
        <Fab
          color='primary'
          size='large'
          style={{ background: 'rgb(145 145 145 / 18%)' }}
        >
          <img src={russia} alt='Russian' style={{ width: '24px' }} />
        </Fab>
          </Fb>
      </Grid>
    </Grid>
  );
};

export default Header;
