import { createTheme, responsiveFontSizes } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    text: {
      primary: 'rgba(245, 245, 245, 0.7)',
    },
    primary: {
      main: '#ffffff',
    },
    secondary: {
      main: '#e61cb3',
    },
    // background: {
    //   default: '#000000'
    // }
  },
  components: {
    MuiButton: {
      styleOverrides: {
        outlined: {
          color: '#ffffff',
          width: '185px',
          height: '35px',
          border: 'none',
          background: 'rgba(112, 112, 112, 0.5)',
          fontFamily: 'Copperplate',
          fontWeight: 'normal',
          fontSize: '16px',
          lineHeight: '16px',
          letterSpacing: '2px',
          ':hover' : {
            background: 'rgb(255 255 255 / 25%)',
            border: 'none',
            transition: '0.2s'
          }
        },
        text: {
          color: '#ffffff',
          width: '100%',
          height: '37px',
          borderRadius: '0px 0px 10px 10px',
          background: '#e61cb3',
          fontFamily: 'GilroyMedium',
          fontSize: '10px',
          ':hover': {
            background: '#c55b03',
          },
        }
      },
    },
    MuiRating: {
      styleOverrides: {
        icon: {
          color: '#e61cb3',
        },
      },
    },
    MuiContainer: {
      styleOverrides: {
        maxWidthLg: {
          minWidth: '100%',
        },
        maxWidthMd: {
          minWidth: '90%',
        },
      },
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          background: 'linear-gradient(180deg, #000001 22.7%, #0D1622 31.57%, #1E0724 65.85%, #1F2A3B 80.31%, #0D1622 90.62%);',
          backgroundColor: '#000001',
        }
      },
    }
  },
});

export default responsiveFontSizes(theme);
