import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import { PRODUCT_UPDATE_RESET } from 'constants/productConstants';
import { listProductDetails, updateProduct } from 'actions/productActions';

import Loader from 'components/Loader';
import Message from 'components/Message';

import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack'
import Input from '@mui/material/Input';
import PhotoCamera from '@mui/icons-material/PhotoCamera';
import IconButton from '@mui/material/IconButton';

import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';

import imaage from 'assets/detailBackground.jpg';

import axios from 'axios';

import useStyles from './styles';
import { Form } from 'react-bootstrap';

const ProductEditScreen = ({ match, history }) => {
  const classes = useStyles();

  const productId = match.params.id;

  const [name, setName] = useState('');
  const [price, setPrice] = useState(0);
  const [image, setImage] = useState('');
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('');
  const [countInStock, setCountInStock] = useState(0);
  const [description, setDescription] = useState('');
  const [uploading, setUploading] = useState(false);

  const dispatch = useDispatch();

  const productDetails = useSelector(state => state.productDetails);
  const { loading, error, product } = productDetails;

  const productUpdate = useSelector(state => state.productUpdate);
  const {
    loading: loadingUpdate,
    error: errorUpdate,
    success: successUpdate,
  } = productUpdate;

  useEffect(() => {
    if (successUpdate) {
      dispatch({ type: PRODUCT_UPDATE_RESET });
      history.push('/admin/productList');
    } else {
      if (!product.name || product._id !== productId) {
        dispatch(listProductDetails(productId));
      } else {
        setName(product.name);
        setPrice(product.price);
        setImage(product.image);
        setBrand(product.brand);
        setCategory(product.category);
        setCountInStock(product.countInStock);
        setDescription(product.description);
      }
    }
  }, [product, dispatch, productId, history, successUpdate]);

  const uploadFileHandler = async(e) => {
    if(!e.target.files) {
      return null
    }
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append('image', file);
    setUploading(true);

    try {
      const config = {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }

      const { data } = await axios.post('/api/upload', formData, config);
      setImage(data);
      setUploading(false)
    }
    catch (error){
      console.log(error);
      setUploading(false);
    }
  }
  const submitHandler = e => {
    e.preventDefault();
    dispatch(
      updateProduct({
        _id: productId,
        name,
        price,
        image,
        brand,
        category,
        countInStock,
        description,
      })
    );
  };

  return (
    <Container
      container
      className={classes.container}
      style={{ background: `url(${imaage}) no-repeat center/cover` }}
    >
      <Container
        component='main'
        maxWidth='xs'
        className={classes.formController}
      >
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar
            sx={{ bgcolor: 'secondary.main', borderRadius: '0' }}
            className={classes.logo}
          >
            <LockOutlinedIcon />
          </Avatar>
          <h3 className={classes.title}>Edit Product</h3>
          {loadingUpdate && <Loader />}
          {errorUpdate && <Message>{errorUpdate}</Message>}
          {loading ? (
            <Loader />
          ) : error ? (
            <Message>{error}</Message>
          ) : (
            <Box
              component='form'
              noValidate
              onSubmit={submitHandler}
              sx={{ mt: 3 }}
            >
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    autoComplete='given-name'
                    name='firstName'
                    required
                    fullWidth
                    id='firstName'
                    label='Имя'
                    type='name'
                    value={name}
                    onChange={e => setName(e.target.value)}
                    autoFocus
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id='price'
                    label='Цена'
                    type='number'
                    name='price'
                    autoComplete='price'
                    value={price}
                    onChange={e => setPrice(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <label htmlFor="image-file" 
                  >
                    <Input
                      accept="image/*"
                      value={image}
                      id="image-file"
                      type="file"
                      onChange={(e) => setImage(e.target.value)}
                    />
                    <Button
                      variant="link"
                      component="span"
                      loading={uploading}
                      onClick={uploadFileHandler}
                      id='image-file'
                    >
                      Choose File
                    </Button>
                  </label>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id='brand'
                    label='Brand'
                    type='text'
                    name='brand'
                    autoComplete='brand'
                    value={brand}
                    onChange={e => setBrand(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id='countInStock'
                    label='countInStock'
                    type='number'
                    name='countInStock'
                    autoComplete='countInStock'
                    value={countInStock}
                    onChange={e => setCountInStock(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id='category'
                    label='category'
                    type='text'
                    name='category'
                    autoComplete='category'
                    value={category}
                    onChange={e => setCategory(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id='description'
                    label='description'
                    type='text'
                    name='description'
                    autoComplete='description'
                    value={description}
                    onChange={e => setDescription(e.target.value)}
                  />
                </Grid>
              </Grid>
              <Button
                type='submit'
                fullWidth
                variant='link'
                style={{ width: '100%', marginTop: '30px' }}
              >
                Update
              </Button>
              <Button
                fullWidth
                variant='link'
                component={Link}
                to='/admin/productList'
                style={{ width: '100%', marginTop: '30px' }}
              >
                Go Back
              </Button>
            </Box>
          )}
        </Box>
      </Container>
    </Container>
  );
};

export default ProductEditScreen;
