import React from 'react';
import { Link } from 'react-router-dom'
import Fb from 'components/Fb';
import Box from '@mui/material/Box';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

import useStyles from './styles';

const ProductCart = ({ product }) => {
  const classes = useStyles();

  return (
    <Box className={classes.cartSection}>
      <div 
        className={classes.imageHolder}
        style={{
          width: '226px',
          height: '218px',
          background: `url(${product.image}) no-repeat center/cover`,
          margin: '0 auto'
        }}
      />

      <Fb px={1.5} py={2} justifyBetween alignCenter>
        {product.countInStock > 0 ? (
          <Stack direction="row" spacing={1}>
            <Chip label={product.type} size="small" variant="outlined" />
            <Chip label={product.weight} size="small" variant="outlined" />
          </Stack>
        ) : (
          'Out of Stock'
        )}
        <Typography>
          <span className={classes.price}>&#1423; {product.price}</span>
        </Typography>
      </Fb>
      <div>
        <Button variant='outlined' className={classes.button}><Link to={`/product/${product._id}`}>Добавить в корзину</Link></Button>
      </div>
    </Box>
  );
};

export default ProductCart;
