import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import Rating from '@mui/material/Rating';
import Button from '@mui/material/Button';
import InputLabel from '@mui/material/InputLabel';
import { Link } from 'react-router-dom';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';

import Message from 'components/Message';
import Loader from '../../components/Loader';
import Fb from 'components/Fb';
import {
  listProductDetails,
  createProductReview,
} from '../../actions/productActions';
import { PRODUCT_CREATE_REVIEW_RESET } from '../../constants/productConstants';

import useStyles from './styles';
import Grid from '@mui/material/Grid';
import image from 'assets/detailBackground.jpg';

const ProductDetailSection = ({ history, match }) => {
  const classes = useStyles();
  const [qty, setQty] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');

  const dispatch = useDispatch();

  const productDetails = useSelector(state => state.productDetails);
  const { loading, error, product } = productDetails;

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  const productReviewCreate = useSelector(state => state.productReviewCreate);
  const {
    success: successProductReview,
    loading: loadingProductReview,
    error: errorProductReview,
  } = productReviewCreate;

  useEffect(() => {
    if (successProductReview) {
      setRating(0);
      setComment('');
    }
    if (!product._id || product._id !== match.params.id) {
      dispatch(listProductDetails(match.params.id));
      dispatch({ type: PRODUCT_CREATE_REVIEW_RESET });
    }
  }, [dispatch, match, successProductReview]);

  const addToCartHandler = () => {
    history.push(`/cart/${match.params.id}?qty=${qty}`);
  };

  const submitHandler = e => {
    e.preventDefault();
    dispatch(
      createProductReview(match.params.id, {
        rating,
        comment,
      })
    );
  };

  // loading ? (
  //   <Loader />
  // ) :

  // <Fb justifyBetween alignCenter style={{marginTop: '20px'}}>
  //             {product.countInStock > 0 ? (
  //               <FormControl size='small' style={{width: '130px'}}>
  //               <InputLabel id="demo-simple-select-label">Quantity</InputLabel>
  //               <Select
  //                 labelId="demo-simple-select-label"
  //                 id="demo-simple-select"
  //                 value={qty}
  //                 onChange={(e) => setQty(e.target.value)}
  //               >
  //                 {
  //                 [...Array(product.countInStock).keys()].map((x) => (
  //                   <MenuItem  value={x + 1} key={x + 1}><p>{x + 1}</p></MenuItem>
  //                 ))
  //               }
  //               </Select>
  //             </FormControl>
  //             ) : (
  //               'Out of Stock'
  //             )}
  //             <Button
  //               variant="outlined"
  //               disabled={product?.countInStock < 1}
  //               style={{textDecoration: 'none!important', border:'none!important'}}
  //               onClick={addToCartHandler}
  //             >
  //               Добавить в корзину
  //             </Button>
  //           </Fb>
  return (
    <Container
      maxWidth='lg'
      style={{
        height: '88vh',
        background: `url(${image}) no-repeat center/cover`,
        paddingTop: '120px',
      }}
    >
      <Link to='/'>
        <Typography
          style={{
            color: 'white',
            marginBottom: '20px',
            fontFamily: 'Copperplate',
            fontWeight: 'bold',
          }}
        >
          Назад
        </Typography>
      </Link>
      {error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Grid
          container
          direction='row'
          justifyContent='space-between'
          alignItems='center'
          spacing={2}
        >
          <Grid md={7} item className={classes.details}>
              <Typography
                style={{
                  color: '#969696',
                  fontSize: '18px',
                  fontFamily: 'Burn',
                }}
              >
                {product.brand}
              </Typography>
              <Typography
                style={{
                  color: 'white',
                  fontSize: '60px',
                  textTransform: 'uppercase',
                  fontFamily: 'Copperplate',
                  fontWeight: 'bold',
                  lineHeight: '90%',
                  width: '318px',
                }}
              >
                {product.name}
              </Typography>
              <Typography variant='p' className={classes.description}>
                {product.description}
              </Typography>
              <Fb mb={5} mt={1}>
                <Stack direction='row' spacing={1}>
                  <Chip
                    label={`${product.type}`}
                    variant='filled'
                    style={{
                      fontFamily: 'Copperplate',
                      background: 'rgb(145 145 145 / 21%)',
                      borderRadius: '0',
                      fontWeight: 'normal',
                      fontSize: '16px',
                    }}
                  />
                  <Chip
                    label={`${product.weight}`}
                    variant='filled'
                    style={{
                      fontFamily: 'Copperplate',
                      background: 'rgb(145 145 145 / 21%)',
                      borderRadius: '0',
                      fontWeight: 'normal',
                      fontSize: '16px',
                    }}
                  />
                </Stack>
              </Fb>
              <Button
                component={Link}
                to={`/product/${product._id}`}
                variant='link'
              >
                Добавить в корзину
              </Button>
          </Grid>
          <Grid md={5} item className={classes.imageHolder}>
            <img className={classes.image} src={product.image} alt='' />
          </Grid>
        </Grid>
      )}
    </Container>
  );
};

export default ProductDetailSection;
