import React, { useState } from 'react';
import useStyles from './styles';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import image from 'assets/detailBackground.jpg';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useSelector, useDispatch } from 'react-redux';
import { saveShippingAddress } from 'actions/cartActions';
import Typography from '@mui/material/Typography';
import CheckoutSteps from 'components/CheckoutSteps/CheckoutSteps';
import Message from 'components/Message';
import { Link } from 'react-router-dom';
import Paper from '@mui/material/Paper'
const PlaceOrder = ({ history }) => {
  const classes = useStyles();

  const cart = useSelector(state => state.cart);

  return (
    <Grid
    container
    md={12}
    justifyContent='space-around'
    className={classes.container}
      style={{
        background: `url(${image}) no-repeat center/cover`,
      }}
    >
        <Grid item md={11} >
          <Typography style={{ textAlign: 'center', fontSize: '30px' }}>
            Разместить заказ
          </Typography>
          <CheckoutSteps step1 step2 step3 step4 />
        </Grid>
      <Grid
        item md={7}
        className={classes.formController}
      >
        <Box
          component='form'
          onSubmit={() => console.log('hey')}
          noValidate
          color='primary'
          sx={{ mt: 1 }}
        >
          <Typography color='secondary'>Shipping</Typography>
          <strong>Address: </strong>
          {cart.shippingAddress.address}, {cart.shippingAddress.city}{' '}
          {cart.shippingAddress.phone},{' '}
          <Typography color='secondary'>Payment Method</Typography>
          <strong>Method: </strong>
          {cart.paymentMethod}
          <Typography color='secondary'>Ordered Items</Typography>
          {cart.cartItems.length === 0 ? (
            <Message>Your Card is Empty</Message>
          ) : (
            <ul>
              {cart.cartItems.map((item, index) => (
                <li key={index}>
                  <Link>{item.name}</Link>
                  <p>
                    {item.qty} x $ {item.price} = $ {item.qty * item.price}
                  </p>
                </li>
              ))}
            </ul>
          )}
        </Box>
      </Grid>
      <Grid className={classes.formController} item md={3}>

      </Grid>
    </Grid>
  );
};

export default PlaceOrder;
