import React, { useState } from 'react';
import useStyles from './styles';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import image from 'assets/detailBackground.jpg';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useSelector, useDispatch } from 'react-redux';
import { saveShippingAddress } from 'actions/cartActions';
import Typography from '@mui/material/Typography';
import CheckoutSteps from 'components/CheckoutSteps/CheckoutSteps';
import Message from 'components/Message';
import { Link } from 'react-router-dom';
import Paper from '@mui/material/Paper';
import { FixedSizeList } from 'react-window';
import List from '@mui/material/List'
const PlaceOrder = ({ history }) => {
  const classes = useStyles();

  const cart = useSelector(state => state.cart);

  return (
    <Container
      md={12}
      className={classes.container}
      style={{
        background: `url(${image}) no-repeat center/cover`,
      }}
    >
      <Grid item md={12}>
        <Typography style={{ textAlign: 'center', fontSize: '30px' }}>
          Разместить заказ
        </Typography>
        <CheckoutSteps step1 step2 step3 step4 />
      </Grid>
      <Grid container md={12} justifyContent='space-around' pt={5}>
        <Grid item md={7} className={classes.formController}>
          <List
            sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
          >
            <ListItem alignItems='flex-start'>
              <ListItemAvatar>
                <Avatar alt='Remy Sharp' src='/static/images/avatar/1.jpg' />
              </ListItemAvatar>
              <ListItemText
                primary='Brunch this weekend?'
                secondary={
                  <React.Fragment>
                    <Typography
                      sx={{ display: 'inline' }}
                      component='span'
                      variant='body2'
                      color='text.primary'
                    >
                      Ali Connors
                    </Typography>
                    {" — I'll be in your neighborhood doing errands this…"}
                  </React.Fragment>
                }
              />
            </ListItem>
            <Divider variant='inset' component='li' />
          </List>
          {/* <Typography color='secondary'>Shipping</Typography>
            <strong>Address: </strong>
            {cart.shippingAddress.address}, {cart.shippingAddress.city}{' '}
            {cart.shippingAddress.phone},{' '}
            <Typography color='secondary'>Payment Method</Typography>
            <strong>Method: </strong>
            {cart.paymentMethod}
            <Typography color='secondary'>Ordered Items</Typography>
            {cart.cartItems.length === 0 ? (
              <Message>Your Card is Empty</Message>
            ) : (
              <ul>
                {cart.cartItems.map((item, index) => (
                  <li key={index}>
                    <Link>{item.name}</Link>
                    <p>
                      {item.qty} x $ {item.price} = $ {item.qty * item.price}
                    </p>
                  </li>
                ))}
              </ul>
            )} */}
        </Grid>
        <Grid className={classes.formController} item md={3}></Grid>
      </Grid>
    </Container>
  );
};

export default PlaceOrder;
