import React, {useEffect, useState, useRef} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from 'actions/productActions';
import Grid from '@mui/material/Grid'
import Button from '@mui/material/Button'
const Slider = () => {
  const dispatch = useDispatch();

  const productList = useSelector(state => state.productList);
  const { loading, error, products } = productList;

  useEffect(() => {
    dispatch(listProducts())
  }, [dispatch])
  const blackBurn = products && products.filter((item) => item.brand === 'Black Burn');

  let activeSlideIndex = 0;

  function changeSlide(direction) {
    if (direction === 'up') {
      activeSlideIndex++
      if (activeSlideIndex === slidesCount) {
        activeSlideIndex = 0;
      }
    }else if (direction === 'down') {
      activeSlideIndex--
      if (activeSlideIndex < 0) {
        activeSlideIndex = slidesCount - 1;
      }
    }

    const height = container.clientHeight

    mainSlide.style.transform = `translateY(-${activeSlideIndex * height}px)`
    sidebar.style.transform = `translateY(${activeSlideIndex * height}px)`
}

  return (
    <Grid container mt={12} spacing={2} style={{ height: '500px' }}>
      <Grid item md={4} style={{background: 'red'}}>
        <Button onClick={() => changeSlide('down')}>DOWN</Button>
      </Grid>
      <Grid item md={8} style={{background: 'black'}}>
      <Button>UP</Button>
      </Grid>
    </Grid>
  )
};

export default Slider;
