import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';

import { logout } from '../../actions/userActions';

import { NavDropdown } from 'react-bootstrap';
import Grid from '@mui/material/Grid';
import Fab from '@mui/material/Fab';
import IconButton from '@mui/material/IconButton';
import LogoutIcon from '@mui/icons-material/Logout';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import Fb from 'components/Fb';
import logo from 'assets/logo.svg';
import Tooltip from '@mui/material/Tooltip';
import Stack from '@mui/material/Stack';
import MenuItem from '@mui/material/MenuItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Menu from '@mui/material/Menu';

import useStyles from './styles';

import russia from 'assets/russia.png';
import { Typography } from '@mui/material';

const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  const logoutHandler = () => {
    dispatch(logout());
  };
  const [isScrolled, setIsScrolled] = useState(false);
  const classes = useStyles({ isScrolled });

  const handleScrollChange = () => {
    const top = document.body.scrollTop || document.documentElement.scrollTop;
    setIsScrolled(top > 100);
  };

  const [anchorEl, setAnchorEl] = useState(null);

  const handleOpenAccountSettings = event => {
    setAnchorEl(event.currentTarget);
  };
  const handleCloseAccountSettings = () => {
    setAnchorEl(null);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScrollChange);
    return () => {
      window.removeEventListener('scroll', handleScrollChange);
    };
  }, []);

  return (
    <Grid
      container
      px={8}
      py={2}
      alignItems='center'
      justifyContent='space-between'
      className={classes.header}
    >
      <Grid item md={3}>
        <Link to='/'>
          <img src={logo} alt='logo' className={classes.logo} />
        </Link>
      </Grid>
      {/* <Route render={({ history }) => <SearchBox history={history} />} /> */}
      <Grid item>
        <Stack direction="row" spacing={1}>
          <Link to='/cart'>
            <Fab
              size='medium'
              color='secondary'
            >
              <ShoppingCartIcon color='primary' />
            </Fab>
          </Link>
          {userInfo ? (
            <>
              <Tooltip title='Account settings'>
                <Fab
                  size='medium'
                  color='secondary'
                  onClick={handleOpenAccountSettings}
                >
                  <Typography>`${userInfo.name}.substring(0, 1)`</Typography>
                </Fab>
              </Tooltip>
              <Menu
                anchorEl={anchorEl}
                open={anchorEl}
                onClose={handleOpenAccountSettings}
                onClick={handleCloseAccountSettings}
                transformOrigin={{ horizontal: 'right', vertical: 'top' }}
                anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
              >
                <MenuItem component={Link} to='/profile' >
                  <ListItemIcon>
                    <PersonOutlineIcon color="secondary" />
                  </ListItemIcon>
                  <ListItemText>Мой Профиль</ListItemText>
                </MenuItem>
                <MenuItem onClick={logoutHandler}>
                  <ListItemIcon>
                    <LogoutIcon color="secondary" />
                  </ListItemIcon>
                  <ListItemText>Выйти</ListItemText>
                </MenuItem>
              </Menu>
            </>
          ) : (
            <Link to='/login'>
              <Fab
                onClick={logoutHandler}
                size='medium'
                color='secondary'
              >
                <PersonOutlineIcon color='primary' />
              </Fab>
            </Link>
          )}
          {userInfo && userInfo.isAdmin && (
            <NavDropdown title='Admin' id='adminmenu'>
              <Link to='/admin/userlist'>
                <NavDropdown.Item>Users</NavDropdown.Item>
              </Link>
              <Link to='/admin/productlist'>
                <NavDropdown.Item>Products</NavDropdown.Item>
              </Link>
              <Link to='/admin/orderlist'>
                <NavDropdown.Item>Orders</NavDropdown.Item>
              </Link>
            </NavDropdown>
          )}
          <Fab
            size='medium'
            color='secondary'
          >
            <img src={russia} alt='Russian' style={{ width: '24px' }} />
          </Fab>
        </Stack>
      </Grid>
    </Grid>
  );
};

export default Header;
