import { Slider, Slide } from 'pure-react-carousel';
import React, { useEffect, useContext, useState, useCallback } from 'react'

import useStyles from './styles';

import {
  CarouselContext,
} from 'pure-react-carousel';

const ProductsImagePreview = ({setActiveProduct, products }) => {
  const classes = useStyles();

  const carouselContext = useContext(CarouselContext);
  const [, setCurrentSlide] = useState(carouselContext.state.currentSlide);

  const onChange = useCallback(() => {
    setActiveProduct(products[carouselContext.state.currentSlide]);
    setCurrentSlide(carouselContext.state.currentSlide);
  }, [carouselContext.state.currentSlide, products, setActiveProduct]);

  useEffect(() => {
    carouselContext.subscribe(onChange);
    return () => carouselContext.unsubscribe(onChange);
  }, [carouselContext, onChange]);

  return (
      <div style={{height: '100%', width: '100%'}}>
        <Slider
      classNameAnimation={classes.animation}
      className={classes.previewWrapper}
    >
      {products.map((product, index) => (

        <Slide 
          key={product._id} 
          index={index} 
          onChange={() =>
            carouselContext.setStoreState({ currentSlide: index })
          }
          >
            <div
              className={classes.imagePreview}
              style={{
                background: `url(${product.image}) no-repeat center/cover`,
              }}
            />            
        </Slide>
      ))}
    </Slider>
      </div>
  );
};

export default ProductsImagePreview;
