import React, { useState } from 'react';
import Container from '@mui/material/Container';
import Fb from 'components/Fb/Component';
import Tabs from '@mui/material/Tabs'
import sortProducts from 'utils/helpers';
import ProductCart from 'components/ProductCard/ProductCard';
import Tab from '@mui/material/Tab';
import Grid from '@mui/material/Grid'
import Typography from '@mui/material/Typography';

const ProductsListScreen = ({ products, }) => {

  const [brand, setBrand] = useState(0);
  const sortedProducts = sortProducts(products)
  const productsEntries = Object.entries(sortedProducts);
  const handleChange = (_, index) => {
    setBrand(index);
  };

  return (
    <Container style={{marginTop: '121px', background: 'linear-gradient(rgb(0, 0, 1) 2%, rgb(13 19 28) 31.57%, rgb(20 7 24) 65.85%,  rgb(13, 22, 34) 90.62%)'}}>
        {productsEntries.length > 0 && (
        <>
        <Typography
          style={{
            fontFamily:'Copperplate',
            fontWeight: 'normal',
            fontSize: '60px',
            lineHeight: '41px',
            color: 'rgba(255, 255, 255, 0.8)',
            marginLeft: '70px'
          }}
        >
          Catalog
        </Typography>
      <Tabs
        value={brand}
        indicatorColor='secondary'
        variant="scrollable"
        scrollButtons
        allowScrollButtonsMobile
        onChange={handleChange}
        style={{
          marginLeft: '30px',
          marginTop: '76px',
          border: 'none'
        }}
      >
        {productsEntries?.map(([key]) => (
          <Tab
            key={key}
            id={key}
            icon={key}
            style={{
              fontFamily:'Copperplate',
              fontWeight: 'normal',
              fontSize: '20px',
              lineHeight: '21px',
              color: 'rgba(255, 255, 255, 0.8)',
              background: 'rgb(145 145 145 / 21%)',
              marginRight: '10px',
              marginBottom: '10px',
              border: 'none',
              textDecoration: 'none',
              '&:hover': {
                outline: 'none',
              },
            }}
          />
        ))}
      </Tabs>
      <Grid maxWidth='md' container spacing={3} style={{margin: '0 auto'}}>
        {productsEntries[brand][1].map((product) => (
          <Grid item xs={6} md={4} key={product._id}>
            <ProductCart product={product} />
          </Grid>
        ))}
      </Grid>
    </>
    )}
    </Container>
  )
};

export default ProductsListScreen;
