import { makeStyles } from '@mui/styles';

export default makeStyles({
  cartSection: {
    height: '400px',
  },
  imageHolder: {
    position: 'relative',
    width: '100%',
    height: '90%',
  },
  name: {
    fontFamily: 'Copperplate',
    fontWeight: 'normal',
    fontSize: '24px',
    lineHeight: '25px',
    color: 'rgba(255, 255, 255, 0.8)',
  },
  price: {
    fontFamily: 'Copperplate',
    color: 'white'
  },
  button: {
    fontFamily: 'Copperplate',
    fontWeight: 'normal',
    fontSize: '16px',
    lineHeight: '16px',
    color: 'white',
    textDecoration: 'none',
    '&:hover': {
      textDecoration: 'none',
      color: 'white'
    }
  }
})
