  },
  brand: {
    color: '#969696',
    fontSize: '18px',
    fontFamily: 'Burn',
  },
  title: {
    color: 'white',
    fontSize: '60px',
    textTransform: 'uppercase',
    fontFamily: 'Copperplate',
    fontWeight: 'bold',
    lineHeight: '90%',
    width: '318px',
  },
