import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import { login } from '../../store/actions/userActions';

import Loader from '../../components/Loader';
import * as yup from 'yup';

import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import Container from '@mui/material/Container';
import TextField from '@mui/material/TextField';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';

import image from 'assets/detailBackground.jpg';
import useStyles from './styles';
import { useTranslations } from 'contexts/translation.context';

const LoginScreen = ({ location, history }) => {
  const classes = useStyles();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { t } = useTranslations();

  validateScheme = yup.object().shape({
    email: yup.string().required('Электронная почта обязательна для заполнения').email('Email введен не корректно '),
    password: yup.string().required('Пароль обязателен для заполнения').matches(/^(?=.*[A-Z])/, "Пароль должен содержать хотя бы одну заглавную букву")
  })

  const dispatch = useDispatch();

  const userLogin = useSelector(state => state.userLogin);
  const { loading, userInfo } = userLogin;

  const redirect = location.search ? location.search.split('=')[1] : '/';

  useEffect(() => {
    if (userInfo) {
      history.push(redirect);
    }
  }, [history, userInfo, redirect]);

  const submitHandler = e => {
    e.preventDefault();
    dispatch(login(email, password));
  };

  return (
    <Grid
      container
      className={classes.container}
      style={{
        background: `url(${image}) no-repeat center/cover`,
      }}
    >
      <Container
        component='main'
        maxWidth='xs'
        className={classes.formController}
      >
        <Avatar
          sx={{ bgcolor: 'secondary.main', borderRadius: '0' }}
          className={classes.logo}
        >
          <LockOutlinedIcon color='primary'/>
        </Avatar>
        <h3 className={classes.title}>{t('sign_in')}</h3>
        {loading ? (
          <Loader />
        ) : (
          <Box
            component='form'
            onSubmit={submitHandler}
            noValidate
            color='primary'
            sx={{ mt: 1 }}
          >
            <TextField
              margin='normal'
              required
              fullWidth
              id='email'
              label={t('email_address')}
              name='email'
              autoComplete='email'
              autoFocus
              type='email'
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
            <TextField
              margin='normal'
              required
              fullWidth
              name='password'
              label={t('password')}
              type='password'
              color='primary'
              id='password'
              value={password}
              onChange={e => setPassword(e.target.value)}
              autoComplete='current-password'
            />
            <Button
              type='submit'
              fullWidth
              variant='link'
              style={{ width: '100%' }}
            >
              {t('sign_in')}
            </Button>
            <Grid container>
              <Grid item mt={4}>
                <Link
                  to={redirect ? `/register?redirect=${redirect}` : '/register'}
                  variant='body2'
                  style={{ color: 'rgba(255, 255, 255, 0.6)' }}
                >
                  {t('dont_have_account')}
                </Link>
              </Grid>
            </Grid>
          </Box>
        )}
      </Container>
    </Grid>
  );
};

export default LoginScreen;
