import { makeStyles } from "@mui/styles";

export default makeStyles({
  modal: {
    background: 'rgb(39, 41, 50, 90%)',
    width: '500px',
    height: '200px',
    margin: '0 auto',
    marginTop: '50vh',
    transform: 'translateY(-50%)',
    padding: '20px'
  },
  heading: {
    textAlign: 'center',
    fontSize: '25px'
  },
  text: {
    textAlign: 'center',
    marginTop: '10px',
    marginBottom: '10px'
  },
  button: {
    margin: '0 auto'
  }
});
