import { makeStyles } from '@mui/styles';

export default makeStyles({
  cartSection: {
    // height: '400px',
    "&:hover": {
      background: 'red'
    }
  },
  imageHolder: {
    position: 'relative',
    width: '100%',
    height: '90%',
  },
  name: {
    fontSize: '24px!important',
  },
  price: {
    fontFamily: 'Copperplate',
    fontWeight: 'normal',
    fontSize: '18px',
    lineHeight: '19px',
    color: 'rgba(255, 255, 255, 0.65);',
    marginTop: '4px',
  },
  button: {
    width: '100%',
    marginTop: '9px',
  },
  addToCart: {
    width: '100%!important',
    marginTop:'9px!important'
  }
})
