import { makeStyles } from '@mui/styles';

export default makeStyles({
  productSection:{
    width: '100%',
    height: '100vh',
    padding: '30px',
  },
  details: {
    display: 'flex',
    justifyContent: 'space-around',
    flexDirection: 'column',
    alignItems: 'center',
    flexWrap: 'wrap',
    color: 'rgba(245, 245, 245, 0.7)',
  },
  imageHolder: {
    width: '220px',
    height: '220px',
    borderRadius: '100%',
    overflow: 'hidden',
    boxShadow: '0px 0px 20px 2px #E9822C',
  },
  image: {
    width: '100%',
    height: '100%',
    display: 'block',
    objectFit: 'contain',
    borderRadius: '100%',
  },
  box: {
    width: '100%',
    minWidth: '200px',
  },
  row: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  nameText: {
    fontSize: '32px',
    fontFamily: 'Burn, sans-serif',
    textTransform: 'uppercase',
    letterSpacing: '2px',
    color: '#ffffff',
  },
  price: {
    fontSize: '26px',
    fontFamily: 'Copperplate, sans-serif',
    textTransform: 'uppercase',
    letterSpacing: '2px',
    color: '#ffffff',
  },
  description: {
    lineHeight: '28px',
    fontSize: '17px',
    fontFamily: 'Copperplate, sans-serif',
  },
  information: {
    width: '100%',
    fontFamily: 'Copperplate',
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '16px',
    padding: '0 13px'
  }
});
