import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';

import { logout } from '../../actions/userActions';

import { NavDropdown } from 'react-bootstrap';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import LogoutIcon from '@mui/icons-material/Logout';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import logo from 'assets/logo.png'

import useStyles from './styles';

import russia from 'assets/russia.png';

const Header = () => {
  const dispatch = useDispatch();

  const userLogin = useSelector(state => state.userLogin);
  const { userInfo } = userLogin;

  const logoutHandler = () => {
    dispatch(logout());
  };
  const [isScrolled, setIsScrolled] = useState(false);
  const classes = useStyles({ isScrolled });

  const handleScrollChange = () => {
    const top = document.body.scrollTop || document.documentElement.scrollTop;
    setIsScrolled(top > 100);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScrollChange);
    return () => {
      window.removeEventListener('scroll', handleScrollChange);
    };
  }, []);

  return (
    <Grid
      container
      px={8}
      py={4}
      alignItems='center'
      justifyContent='space-between'
      className={classes.header}
    >
      <Grid item md={3}>
        {/* <Link to='/'>
          <h1
            className={classes.title}
          >
            The Capital Shop
          </h1>
        </Link> */}
        <img src={logo} alt="logo" className={classes.logo} style={{
          width: '100px',
          height: '90px',
          opacity: '70%',
          '&:hover': {
            opacity: '100%'
          }
          }}/>
      </Grid>
      {/* <Route render={({ history }) => <SearchBox history={history} />} /> */}
      <Grid>
        <Link to='/cart'>
          <IconButton
            size='large'
            color='primary'
            style={{
              background: 'rgb(145 145 145 / 18%)',
              marginRight: '10px',
            }}
          >
            <ShoppingCartIcon />
          </IconButton>
        </Link>
        {userInfo ? (
          <NavDropdown title={userInfo.name} id='username'>
            <Link to='/profile'>
              <IconButton
                color='primary'
                size='large'
                style={{
                  background: 'rgb(145 145 145 / 18%)',
                  marginRight: '10px',
                }}
              >
                <PersonOutlineIcon />
              </IconButton>
            </Link>
            <IconButton
              onClick={logoutHandler}
              color='primary'
              size='large'
              style={{
                background: 'rgb(145 145 145 / 18%)',
                marginRight: '10px',
              }}
            >
              <LogoutIcon />
            </IconButton>
          </NavDropdown>
        ) : (
          <Link to='/login'>
            <IconButton
              onClick={logoutHandler}
              color='primary'
              size='large'
              style={{
                background: 'rgb(145 145 145 / 18%)',
                marginRight: '10px',
              }}
            >
              <PersonOutlineIcon />
            </IconButton>
          </Link>
        )}
        {userInfo && userInfo.isAdmin && (
          <NavDropdown title='Admin' id='adminmenu'>
            <Link to='/admin/userlist'>
              <NavDropdown.Item>Users</NavDropdown.Item>
            </Link>
            <Link to='/admin/productlist'>
              <NavDropdown.Item>Products</NavDropdown.Item>
            </Link>
            <Link to='/admin/orderlist'>
              <NavDropdown.Item>Orders</NavDropdown.Item>
            </Link>
          </NavDropdown>
        )}
        <IconButton
          color='primary'
          size='large'
          style={{ background: 'rgb(145 145 145 / 18%)' }}
        >
          <img src={russia} alt='Russian' style={{ width: '24px' }} />
        </IconButton>
      </Grid>
    </Grid>
  );
};

export default Header;
