import React, { useState } from 'react';

import sortProducts from 'utils/helpers';

import ProductCart from 'components/ProductCard/ProductCard';

import Tab from '@mui/material/Tab';
import Grid from '@mui/material/Grid'
import Tabs from '@mui/material/Tabs'
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';

import useStyles from './styles';

const ProductsListScreen = ({ products, loading}) => {

  const classes = useStyles();

  const [brand, setBrand] = useState(0);
  const sortedProducts = sortProducts(products)
  const productsEntries = Object.entries(sortedProducts);
  const handleChange = (_, index) => {
    setBrand(index);
  };

  return (
    <Container 
      className={classes.container}
      >
        {productsEntries.length > 0 && (
        <>
        <Typography
          className={classes.catalog}
        >
          Catalog
        </Typography>
      <Tabs
        value={brand}
        indicatorColor='secondary'
        variant="scrollable"
        textColor='inherit'
        scrollButtons
        allowScrollButtonsMobile
        onChange={handleChange}
        className={classes.tabs}
      >
        {productsEntries?.map(([key]) => (
          <Tab
            component='h1'
            key={key}
            id={key}
            icon={key}
          />
        ))}
      </Tabs>
      <Grid container spacing={6} rowSpacing={17} style={{padding: '60px 50px 0 70px'}}>
        {productsEntries[brand][1].map((product) => (
            <ProductCart product={product} key={product.id} loading={loading}/>
        ))}
      </Grid>
    </>
    )}
    </Container>
  )
};

export default ProductsListScreen;
