import React, { useState, useEffect, useMemo } from 'react';

import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from 'actions/productActions';

import Fb from 'components/Fb';
import Rating from 'components/Rating';

import Chip from '@mui/material/Chip';
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container'
import Typography from '@mui/material/Typography';

import useStyles from './styles';
import image from '../../assets/detailBackground.jpg';
import topArrow from '../../assets/topArrow.png';
import bottomArrow from '../../assets/bottomArrow.png';

const Slider = () => {
  const classes = useStyles();
  const [activeSlide, setActiveSlide] = useState(0);
  const dispatch = useDispatch();
  const { loading, error, products: blackBurn  } = useSelector(state => state.productList);
  // const blackBurn = products && products.filter(item => item.brand === 'Black Burn');
  const slidesCount = useMemo(() => blackBurn?.length, [blackBurn]);

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

  const changeSlide = direction => {
    setActiveSlide(prev => {
      if (direction === 'up') {
        return prev >= slidesCount - 1 ? 0 : prev + 1;
      } else if (direction === 'down') {
        return prev < 0 ? slidesCount : prev--;
      }
    })
  };

  return (
    <Container   
      container
      className={classes.container}
      style={{background: `url(${image}) no-repeat center/cover`}}
    >
      <Grid 
      container
      md={11}
      className={classes.mainGrid}
      >
       <img src={topArrow} alt ='SlideTop' style={{width: '1px', height: '100px', position: 'absolute', right: '65%', zIndex: '1000'}}/>
      <Grid
        item
        md={4}
        ml={2}
        className={classes.sidebar}
        style={{ transform: `translateY(-${activeSlide * 100}%)` }}
      >
        {blackBurn && blackBurn?.map(product => (
          <div
            className={classes.sideBarDiv}
            key={product.id}
            style={{
              background: `url(${product.image}) no-repeat center/cover`,
            }}
          />
        ))}
      </Grid>
      <Grid 
        item 
        md={8} 
        className={classes.mainSlide} 
        style={{
          top: `-${(slidesCount - 1) * 100}%`,
          transform: `translateY(${activeSlide * 100}%)`,
        }}
      >
        {[...blackBurn].reverse().map(product => (
          <Fb
            style={{ background: 'rgba(99,99,99, 0.15)' }}
            className={classes.mainSlideDiv}
            key={product.id}
            column
            pt={12}
            p={6}
          >
            <Typography
              style={{
                color: '#969696',
                fontSize: '18px',
                fontFamily: 'Burn',
                marginBottom: '10px',
              }}
            >
              {product.brand}
            </Typography>
            <Typography
              style={{ color: 'white', fontSize: '20px', fontFamily: 'Burn' }}
            >
              {product.name}
            </Typography>
            <Fb mb={2}>
              <Rating
                value={product.rating}
                precision={0.5}
                readOnly
                color='#e61cb3'
              />
            </Fb>
            <Typography
              variant='p'
              style={{
                fontFamily: 'Copperplate',
                fontSize: '17px',
                marginBottom: '10px',
              }}
            >
              {product.description}
            </Typography>
            <Fb mb={6}>
              <Stack direction='row' spacing={1}>
                <Chip
                  label='Сладкий'
                  size='small'
                  variant='outlined'
                  style={{ fontFamily: 'Copperplate' }}
                />
                <Chip
                  label='200g'
                  size='small'
                  variant='outlined'
                  style={{ fontFamily: 'Copperplate' }}
                />
              </Stack>
            </Fb>
            <Button
              onClick={() => changeSlide('up')}
              style={{
                width: '150px',
                borderRadius: '20px',
                fontFamily: 'Copperplate',
                fontSize: '12px',
              }}
            >
              Add to Cart
            </Button>
          </Fb>
        ))}
      </Grid>
    </Grid>
    </Container>
  );
};

export default Slider;
