import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import Rating from '@mui/material/Rating';
import Button from '@mui/material/Button';
import InputLabel from '@mui/material/InputLabel';
import { Link } from 'react-router-dom'
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Container from '@mui/material/Container'
import Typography from '@mui/material/Typography';

import Message from 'components/Message'
import Loader from '../../components/Loader'
import Fb from 'components/Fb';
import { listProductDetails, createProductReview } from '../../actions/productActions'
import { PRODUCT_CREATE_REVIEW_RESET } from '../../constants/productConstants'

import useStyles from './styles';
import Grid from '@mui/material/Grid';
import image from 'assets/detailBackground.jpg'

const ProductDetailSection = ({ history, match}) => {
  const classes = useStyles();
  const [qty, setQty] = useState(1)
  const [rating, setRating] = useState(0)
  const [comment, setComment] = useState('')

  const dispatch = useDispatch()

  const productDetails = useSelector((state) => state.productDetails)
  const { loading, error, product } = productDetails

  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin

  const productReviewCreate = useSelector((state) => state.productReviewCreate)
  const {
    success: successProductReview,
    loading: loadingProductReview,
    error: errorProductReview,
  } = productReviewCreate

  useEffect(() => {
    if (successProductReview) {
      setRating(0)
      setComment('')
    }
    if (!product._id || product._id !== match.params.id) {
      dispatch(listProductDetails(match.params.id))
      dispatch({ type: PRODUCT_CREATE_REVIEW_RESET })
    }
  }, [dispatch, match, successProductReview])

  const addToCartHandler = () => {
    history.push(`/cart/${match.params.id}?qty=${qty}`)
  }

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(
      createProductReview(match.params.id, {
        rating,
        comment,
      })
    )
  }

  return (
    <Container style={{height: '88vh', paddingTop: '120px'}}>
    <div className={classes.productSection} style={{background: `url(${image}) no-repeat center/cover`}}>
      <Link to="/">
        <Typography style={{color: 'white', marginBottom: '20px', fontFamily: 'Copperplate', fontWeight: 'bold'}}>
          GO BACK
        </Typography>
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Grid 
          container
          direction="row"
          justifyContent="space-between"
          alignItems="center"
        >
          <Grid md item className={classes.imageHolder}>
            <img className={classes.image} src={product.image} alt="" />
          </Grid>
          <Grid xs item className={classes.imageDetails}>
            <Typography variant="h3" style={{fontFamily: 'Burn', marginBottom: '20px', color: 'white'}}>{product.name}</Typography>
            <Typography variant='p' style={{fontFamily: 'Copperplate'}}>{product.description}</Typography>
            <Fb>
            <Rating
                value={product.rating}
                text={`${product.numReviews} reviews`}
            />
            </Fb>
            <Fb justifyBetween alignCenter style={{marginTop: '20px'}}>

              {product.countInStock > 0 ? (
                <FormControl size='small' style={{width: '130px'}}>
                <InputLabel id="demo-simple-select-label">Quantity</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={qty}
                  onChange={(e) => setQty(e.target.value)}
                >
                  {
                  [...Array(product.countInStock).keys()].map((x) => (
                    <MenuItem  value={x + 1} key={x + 1}><p>{x + 1}</p></MenuItem>
                  ))
                }
                </Select>
              </FormControl>
              ) : (
                'Out of Stock'
              )}
              <Button 
                variant="outlined" 
                disabled={product?.countInStock < 1} 
                style={{textDecoration: 'none!important', border:'none!important'}}
                onClick={addToCartHandler}
              >
                Добавить в корзину
              </Button>
            </Fb>
          </Grid>
        </Grid>
      )}
    </div>
    </Container>
  );
};

export default ProductDetailSection;
