import React, { useState, useEffect } from 'react'
import { Route } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { Nav, NavDropdown } from 'react-bootstrap'
import SearchBox from '../SearchBox'
import { logout } from '../../actions/userActions';

import Box from '@mui/material/Box';
import Badge from '@mui/material/Badge';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid'
import IconButton from '@mui/material/IconButton';
import SearchIcon from '@mui/icons-material/Search';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Typography from '@mui/material/Typography'
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import Fb from 'components/Fb';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import LogoutIcon from '@mui/icons-material/Logout';
import useStyles from './styles';
import LoginIcon from '@mui/icons-material/Login';
const Header = () => {
  const dispatch = useDispatch()

  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin

  const logoutHandler = () => {
    dispatch(logout())
  }
  const [isScrolled, setIsScrolled] = useState(false);
  const classes = useStyles({ isScrolled });

  const handleScrollChange = () => {
    const top = document.body.scrollTop || document.documentElement.scrollTop;
    setIsScrolled(top > 100);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScrollChange);
    return () => {
      window.removeEventListener('scroll', handleScrollChange);
    };
  }, []);

  return (
    <Grid component="header" className={classes.header} container>
          <Grid item  >
            <Link to='/'>
              <Typography style={{color: 'white', fontFamily: 'Copperplate', fontWeight: 'bold', fontSize: '22px'}}>
                The Capital Shop
              </Typography>
            </Link>
          <Fb justifyBetween>
            {/* <Route render={({ history }) => <SearchBox history={history} />} /> */}
              <Link to='/cart'>
                  <IconButton size="large" color='primary' style={{background: 'rgb(145 145 145 / 18%)'}}>
                    <ShoppingCartIcon />
                  </IconButton>
              </Link>
              {userInfo ? (
                <NavDropdown title={userInfo.name} id='username'>
                  <Link to='/profile'>
                    <IconButton color="primary" size="large" style={{background: 'rgb(145 145 145 / 18%)'}}>
                      <PersonOutlineIcon />
                    </IconButton>           
                 </Link>
                   <IconButton onClick={logoutHandler} color="primary" size="large" style={{background: 'rgb(145 145 145 / 18%)'}}>
                      <LogoutIcon />
                  </IconButton>   

                </NavDropdown>
              ) : (
                <Link to='/login'>
                  <IconButton onClick={logoutHandler} color="primary" size="large" style={{background: 'rgb(145 145 145 / 18%)'}}>
                <PersonOutlineIcon />
            </IconButton>   
                </Link>
              )}
              {userInfo && userInfo.isAdmin && (
                <NavDropdown title='Admin' id='adminmenu'>
                  <Link to='/admin/userlist'>
                    <NavDropdown.Item>Users</NavDropdown.Item>
                  </Link>
                  <Link to='/admin/productlist'>
                    <NavDropdown.Item>Products</NavDropdown.Item>
                  </Link>
                  <Link to='/admin/orderlist'>
                    <NavDropdown.Item>Orders</NavDropdown.Item>
                  </Link>
                </NavDropdown>
              )}
            </Fb>
            </Grid>      
    </Grid>
  )
}

export default Header
