import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import { register } from 'store/actions/userActions';

import Loader from 'components/Loader';
import Message from 'components/Message';
import { registerValidationSchema } from 'hooks/validator';

import { yupResolver } from '@hookform/resolvers/yup';
import { useForm } from 'react-hook-form';

import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';

import image from 'assets/detailBackground.jpg';

import useStyles from './styles';
import { useTranslations } from 'contexts/translation.context';

const RegistrationScreen = ({ location, history }) => {
  const classes = useStyles();
  const { t } = useTranslations();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState(null);

  const { register, handleSubmit, formState: {errors} } = useForm({
    mode: 'onChange',
    resolver: yupResolver(registerValidationSchema)
  });

  const dispatch = useDispatch();

  const userRegister = useSelector(state => state.userRegister);
  const { loading, error, userInfo } = userRegister;

  const redirect = location.search ? location.search.split('=')[1] : '/';

  useEffect(() => {
    if (userInfo) {
      history.push(redirect);
    }
  }, [history, userInfo, redirect]);

  const submitHandler = e => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage('Passwords do not match');
    } else {
      dispatch(register(name, email, password));
    }
  };

  return (
    <Container
      className={classes.container}
      style={{ background: `url(${image}) no-repeat center/cover` }}
    >
      <Container
        component='main'
        maxWidth='xs'
        className={classes.formController}
      >
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar
            sx={{ bgcolor: 'secondary.main', borderRadius: '0' }}
            className={classes.logo}
          >
            <LockOutlinedIcon />
          </Avatar>
          <h3 className={classes.title}>Sign up</h3>
          {message && <Message variant='danger'>{message}</Message>}
          {error && <Message variant='danger'>{error}</Message>}
          {loading && <Loader />}
          <Box
            component='form'
            noValidate
            onSubmit={submitHandler}
            sx={{ mt: 3 }}
          >
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  autoComplete='given-name'
                  name='firstName'
                  required
                  fullWidth
                  id='firstName'
                  label={t('name')}
                  type='name'
                  value={name}
                  onChange={e => setName(e.target.value)}
                  autoFocus

                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  id='email'
                  label={t('email_address')}
                  type='email'
                  name='email'
                  autoComplete='email'
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name='password'
                  label={t('password')}
                  type='password'
                  id='password'
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                  autoComplete='on'
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id='confirmPassword'
                  label={t('confirm_password')}
                  type='password'
                  autoComplete='on'
                  value={confirmPassword}
                  onChange={e => setConfirmPassword(e.target.value)}
                />
              </Grid>
            </Grid>
            <Button
              type='submit'
              fullWidth
              variant='link'
              style={{ width: '100%', marginTop: '30px' }}
              disabled={password !== confirmPassword}
            >
              {t('sign_up')}
            </Button>
            <Grid container justifyContent='flex-end'>
              <Grid item style={{ marginTop: '10px' }}>
                <Link
                  to={redirect ? `/login?redirect=${redirect}` : '/login'}
                  variant='body2'
                  style={{ color: 'white' }}
                >
                  {t('already_have_account')}
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
      </Container>
    </Container>
  );
};

export default RegistrationScreen;
