import { makeStyles } from '@mui/styles';

export default makeStyles({
  productSection: {
    backdropFilter: 'blur(3px)',
    background: 'transparent',
    padding: '30px',
    marginTop:'90px',
    borderRadius: '20px',
    boxShadow: ' -1px 0px 30px -1px rgba(247, 126, 11, 0.4);'
  },
  imageHolder: {
    maxWidth: '100%'
  },
  image: {
    width: '400px',
    height: '400px',
    borderRadius: '46% 54% 5% 95% / 43% 60% 40% 57%'
  }
});
