import React, { useState, useEffect, useRef } from 'react';

import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from 'actions/productActions';

import Fb from 'components/Fb';
import Rating from 'components/Rating';

import Chip from '@mui/material/Chip';
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

import useStyles from './styles';
import image from '../../assets/detailBackground.jpg';

const Slider = () => {
  const classes = useStyles();
  const [activeSlide, setActiveSlide] = useState(0);
  const dispatch = useDispatch();
  const { loading, error, products: blackBurn  } = useSelector(state => state.productList);
  // const blackBurn = products && products.filter(item => item.brand === 'Black Burn');
  const slidesCount = blackBurn?.length;

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

  const changeSlide = direction => {
    setActiveSlide(prev => {
      if (direction === 'up') {
        return prev >= slidesCount - 1 ? 0 : prev + 1;
      } else if (direction === 'down') {
        return prev < 0 ? slidesCount : prev--;
      }
    })
  };

  if (!blackBurn?.length) {
    return null;
  }

  return (
    <Grid 
      container 
      mt={12} 
      className={classes.container}
    >
      <Grid
        item
        md={5}
        className={classes.sidebar}
        style={{ transform: `translateY(-${activeSlide * 100}%)` }}
      >
        {blackBurn.map(product => (
          <div
            className={classes.sideBarDiv}
            key={product.id}
            style={{
              background: `url(${product.image}) no-repeat center/cover`,
            }}
          />
        ))}
      </Grid>
      <Grid 
        item 
        md={7} 
        className={classes.mainSlide} 
        style={{
          top: `-${(slidesCount - 1) * 100}%`,
          transform: `translateY(${activeSlide * 100}%)`,
          height: `${slidesCount - 1}00%`,
          display: 'flex',
          flexDirection: 'column-revers'
        }}
      >
        {blackBurn.map(product => (
          <Fb
            style={{ background: `url(${image}) no-repeat center/cover`  }}
            className={classes.mainSlideDiv}
            key={product.id}
            column
            pt={12}
            p={6}
          >
            <Typography
              style={{
                color: '#969696',
                fontSize: '18px',
                fontFamily: 'Burn',
                marginBottom: '10px',
              }}
            >
              {product.brand}
            </Typography>
            <Typography
              style={{ color: 'white', fontSize: '20px', fontFamily: 'Burn' }}
            >
              {product.name}
            </Typography>
            <Fb mb={2}>
              <Rating
                value={product.rating}
                precision={0.5}
                readOnly
                color='#e61cb3'
              />
            </Fb>
            <Typography
              variant='p'
              style={{
                fontFamily: 'Copperplate',
                fontSize: '17px',
                marginBottom: '10px',
              }}
            >
              {product.description}
            </Typography>
            <Fb mb={6}>
              <Stack direction='row' spacing={1}>
                <Chip
                  label='Сладкий'
                  size='small'
                  variant='outlined'
                  style={{ fontFamily: 'Copperplate' }}
                />
                <Chip
                  label='200g'
                  size='small'
                  variant='outlined'
                  style={{ fontFamily: 'Copperplate' }}
                />
              </Stack>
            </Fb>
            <Button
              onClick={() => changeSlide('up')}
              style={{
                width: '150px',
                borderRadius: '20px',
                fontFamily: 'Copperplate',
                fontSize: '12px',
              }}
            >
              Add to Cart
            </Button>
          </Fb>
        ))}
      </Grid>
    </Grid>
  );
};

export default Slider;
