import { makeStyles } from '@mui/styles';

const MAIN_SLIDER_HEIGHT = 700;

export default makeStyles((theme) => ({
  mainCarousel: {
    width: '100%',
  },
  previewWrapper: {
    height: MAIN_SLIDER_HEIGHT
  },
  imagePreview: {
    width: '100%',
    maxHeight: '480px',
    height: MAIN_SLIDER_HEIGHT,
  },
  slideButton: {
    border: '1px solid #ffffffe6',
    borderRadius: '100%',
    color: '#ffffffe6',
    width: '40px',
    height: '40px',
    background: 'none',
    '&[disabled]': {
      pointerEvents: 'none',
      opacity: 0.4,
    },
    '& > button': {
      border: '1px solid white',
    },
  },
  dots: {
    display: 'flex',
    margin: '0 auto',
    marginTop: '15px',
    width: '97%',
    '& > button': {
       width: '100px!important',
       border: '1px solid #000000',
    }
  },
  animation: {
    transition: 'transform 3000ms cubic-bezier(0.645, 0.045, 0.355, 1) 0s',
  },
  headText: {
    fontFamily: 'Burn!important',
    fontSize: '50px!important',
    textTransform: 'uppercase',
    letterSpacing: '5px!important'
  },
  description: {
    fontFamily: 'Copperplate',
    color: '#f6f7fb',
    fontSize: '19px',
    fontWeight: '300',
    textOverflow: 'ellipsis',
    overflow: 'hidden',
    display: '-webkit-box !important',
    WebkitBoxOrient: 'vertical',
    WebkitLineClamp: '5',
    whiteSpace: 'normal',
    textAlign: 'left!important',
    textTransform: 'capitalize',
  }
}));
